// Frontend/components/Navbar.jsx
import React from 'react';
import { Link, useNavigate } from 'react-router-dom';

const Navbar = () => {
  const token = localStorage.getItem('token');
  const adminToken = localStorage.getItem('adminToken');
  const navigate = useNavigate();

  const handleLogout = () => {
    localStorage.clear();
    alert('Logged out');
    navigate('/login');
  };

  return (
    <nav style={{
      width: "100%",
      padding: "12px 24px",
      backgroundColor: "rgba(0, 0, 0, 0.6)",
      borderBottom: "2px solid #00ffff",
      boxShadow: "0 0 12px #00ffff",
      display: "flex",
      justifyContent: "space-between",
      alignItems: "center",
      fontFamily: "'Orbitron', sans-serif",
      position: "sticky",
      top: 0,
      zIndex: 1000
    }}>
      <div style={{ color: "#00ffff", fontSize: "20px", fontWeight: "bold", textShadow: "0 0 6px #00ffff" }}>
        üìö Library System
      </div>

      <div style={{ display: "flex", gap: "16px", alignItems: "center" }}>
        <Link to="/" style={linkStyle}>üè† Home</Link>
        <Link to="/booklist" style={linkStyle}>üìñ Books</Link>

        {adminToken ? (
          <>
            <Link to="/admin-dashboard" style={linkStyle}>üõ† Admin</Link>
            <button onClick={handleLogout} style={buttonStyle}>üö™ Logout</button>
          </>
        ) : token ? (
          <>
            <Link to="/user" style={linkStyle}>üë§ Profile</Link>
            <button onClick={handleLogout} style={buttonStyle}>üö™ Logout</button>
          </>
        ) : (
          <>
            <Link to="/login" style={linkStyle}>üîê Login</Link>
            <Link to="/register" style={linkStyle}>üìù Register</Link>
            <Link to="/admin-login" style={linkStyle}>üõ† Admin Login</Link>
          </>
        )}
      </div>
    </nav>
  );
};

const linkStyle = {
  color: "#00ffff",
  textDecoration: "none",
  fontWeight: "bold",
  fontSize: "14px",
  textShadow: "0 0 4px #00ffff",
  transition: "color 0.3s ease",
};

const buttonStyle = {
  padding: "6px 12px",
  fontSize: "13px",
  backgroundColor: "transparent",
  border: "1px solid #00ffff",
  color: "#00ffff",
  borderRadius: "6px",
  cursor: "pointer",
  boxShadow: "0 0 6px #00ffff",
  fontWeight: "bold"
};

export default Navbar;
