import React, { useEffect, useState } from 'react';
import { Link, useNavigate, useLocation } from 'react-router-dom';

const Navbar = () => {
  const token = localStorage.getItem('token');
  const adminToken = localStorage.getItem('adminToken');
  const navigate = useNavigate();
  const location = useLocation();
  const [activePath, setActivePath] = useState(location.pathname);

  useEffect(() => {
    setActivePath(location.pathname);
  }, [location]);

  const handleLogout = () => {
    localStorage.clear();
    alert('Logged out');
    navigate('/login');
  };

  const navItems = [
    { to: "/", label: "🏠 Home" },
    { to: "/booklist", label: "📖 Books" },
    ...(adminToken
      ? [{ to: "/admin-dashboard", label: "🛠 Admin" }]
      : token
        ? [{ to: "/user", label: "👤 Profile" }]
        : [
            { to: "/login", label: "🔐 Login" },
            { to: "/register", label: "📝 Register" },
            { to: "/admin-login", label: "🛠 Admin Login" }
          ])
  ];

  return (
    <nav style={{
      position: "relative",
      padding: "12px 24px",
      backgroundColor: "#000",
      borderBottom: "1px solid #00ffff",
      boxShadow: "0 0 10px #00ffff",
      fontFamily: "'Orbitron', sans-serif",
      zIndex: 10
    }}>
      <div style={{
        display: "flex",
        justifyContent: "space-between",
        alignItems: "center"
      }}>
        <div style={{
          color: "#00ffff",
          fontSize: "20px",
          textShadow: "0 0 6px #00ffff"
        }}>
          📚 Library System
        </div>

        <ul style={{
          display: "flex",
          gap: "32px",
          listStyle: "none",
          margin: 0,
          padding: 0,
          position: "relative"
        }}>
          {navItems.map(({ to, label }) => (
            <li key={to} style={{ position: "relative" }}>
              <Link to={to} style={{
                color: "#00ffff",
                textDecoration: "none",
                fontSize: "14px",
                paddingBottom: "6px",
                textShadow: "0 0 4px #00ffff",
                position: "relative"
              }}>
                {label}
              </Link>
              {activePath === to && (
                <span style={{
                  position: "absolute",
                  bottom: 0,
                  left: 0,
                  width: "100%",
                  height: "2px",
                  background: "linear-gradient(90deg, #00ffff, #00ffff)",
                  boxShadow: "0 0 8px #00ffff",
                  animation: "beamSlide 0.4s ease-out"
                }} />
              )}
            </li>
          ))}
          {(adminToken || token) && (
            <li>
              <button onClick={handleLogout} style={{
                background: "transparent",
                border: "1px solid #00ffff",
                color: "#00ffff",
                padding: "6px 12px",
                borderRadius: "4px",
                cursor: "pointer",
                fontSize: "13px",
                boxShadow: "0 0 6px #00ffff",
                textShadow: "0 0 4px #00ffff"
              }}>🚪 Logout</button>
            </li>
          )}
        </ul>
      </div>

      {/* Beam animation keyframes */}
      <style>{`
        @keyframes beamSlide {
          0% {
            transform: scaleX(0);
            opacity: 0.2;
          }
          50% {
            transform: scaleX(1.2);
            opacity: 1;
          }
          100% {
            transform: scaleX(1);
            opacity: 1;
          }
        }
      `}</style>
    </nav>
  );
};

export default Navbar;
