// Frontend/App.jsx
import React, { useState, useEffect } from 'react';
import './styles.css';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';

import Navbar from './components/Navbar';
import Home from './pages/Home';
import Login from './pages/Login';
import Register from './pages/Register';
import BookList from './pages/BookList';
import User from './pages/User';
import AdminLogin from './pages/AdminLogin'; 
import AdminDashboard from './pages/AdminDashboard'; 
import BookReader from './pages/BookReader'; // ✅ NEW

function App() {
  const [toast, setToast] = useState(null);

  useEffect(() => {
    window.showToast = (message, type = "info") => {
      setToast({ message, type });
      setTimeout(() => setToast(null), 3000);
    };
  }, []);

  return (
    <Router>
      <Navbar />

      <main className="container">
        <Routes>
          {/* Public Routes */}
          <Route path="/" element={<Home />} />
          <Route path="/booklist" element={<BookList />} />
          <Route path="/login" element={<Login />} />
          <Route path="/register" element={<Register />} />
          <Route path="/user" element={<User />} />

          {/* Read Book Route */}
          <Route path="/read/:bookId" element={<BookReader />} /> {/* ✅ NEW */}

          {/* Admin Routes */}
          <Route path="/admin-login" element={<AdminLogin />} />
          <Route path="/admin-dashboard" element={<AdminDashboard />} />
        </Routes>
      </main>

      {toast && (
        <div style={{
          position: "fixed",
          top: "20px",
          right: "20px",
          backgroundColor: "rgba(0,0,0,0.8)",
          color: toast.type === "error" ? "#ff0066" : toast.type === "success" ? "#00ffcc" : "#00ffff",
          border: `2px solid ${toast.type === "error" ? "#ff0066" : toast.type === "success" ? "#00ffcc" : "#00ffff"}`,
          padding: "10px 16px",
          borderRadius: "8px",
          fontFamily: "'Orbitron', sans-serif",
          fontSize: "14px",
          boxShadow: `0 0 12px ${toast.type === "error" ? "#ff0066" : toast.type === "success" ? "#00ffcc" : "#00ffff"}`,
          zIndex: 9999,
          animation: "fadeIn 0.3s ease-out"
        }}>
          {toast.message}
          <style>{`
            @keyframes fadeIn {
              from { opacity: 0; transform: translateY(-10px); }
              to { opacity: 1; transform: translateY(0); }
            }
          `}</style>
        </div>
      )}
    </Router>
  );
}

export default App;
