import React from 'react';
import { Link, useNavigate, useLocation } from 'react-router-dom';

const Navbar = () => {
  const token = localStorage.getItem('token');
  const adminToken = localStorage.getItem('adminToken');
  const navigate = useNavigate();
  const location = useLocation();

  const handleLogout = () => {
    localStorage.clear();
    alert('Logged out');
    navigate('/login');
  };

  const isActive = (path) => location.pathname === path;

  return (
    <nav style={{
      position: "relative",
      padding: "12px 24px",
      backgroundColor: "#000",
      borderBottom: "1px solid #00ffff",
      boxShadow: "0 0 10px #00ffff",
      display: "flex",
      justifyContent: "space-between",
      alignItems: "center",
      fontFamily: "'Orbitron', sans-serif",
      zIndex: 10
    }}>
      <div style={{ color: "#00ffff", fontSize: "20px", textShadow: "0 0 6px #00ffff" }}>
        ðŸ“š Library System
      </div>

      <div style={{ display: "flex", gap: "24px", alignItems: "center" }}>
        <NavItem to="/" label="ðŸ  Home" active={isActive("/")} />
        <NavItem to="/booklist" label="ðŸ“– Books" active={isActive("/booklist")} />

        {adminToken ? (
          <>
            <NavItem to="/admin-dashboard" label="ðŸ›  Admin" active={isActive("/admin-dashboard")} />
            <button onClick={handleLogout} style={buttonStyle}>ðŸšª Logout</button>
          </>
        ) : token ? (
          <>
            <NavItem to="/user" label="ðŸ‘¤ Profile" active={isActive("/user")} />
            <button onClick={handleLogout} style={buttonStyle}>ðŸšª Logout</button>
          </>
        ) : (
          <>
            <NavItem to="/login" label="ðŸ” Login" active={isActive("/login")} />
            <NavItem to="/register" label="ðŸ“ Register" active={isActive("/register")} />
            <NavItem to="/admin-login" label="ðŸ›  Admin Login" active={isActive("/admin-login")} />
          </>
        )}
      </div>
    </nav>
  );
};

const NavItem = ({ to, label, active }) => (
  <Link to={to} style={{
    position: "relative",
    color: "#00ffff",
    textDecoration: "none",
    fontSize: "14px",
    paddingBottom: "4px",
    textShadow: "0 0 4px #00ffff",
    borderBottom: active ? "2px solid #00ffff" : "2px solid transparent",
    transition: "border-bottom 0.3s ease"
  }}>
    {label}
  </Link>
);

const buttonStyle = {
  background: "transparent",
  border: "1px solid #00ffff",
  color: "#00ffff",
  padding: "6px 12px",
  borderRadius: "4px",
  cursor: "pointer",
  fontSize: "13px",
  boxShadow: "0 0 6px #00ffff",
  textShadow: "0 0 4px #00ffff"
};

export default Navbar;
