// client/src/pages/Register.jsx
import React, { useState, useEffect } from "react";
import "particles.js";
import axios from "axios";

const Register = () => {
  const [form, setForm] = useState({
    name: "",
    email: "",
    password: "",
    accessCode: ""
  });
  const [error, setError] = useState("");
  const ADMIN_CODE = "123456"; // Replace with your actual admin-defined code

  useEffect(() => {
    const interval = setInterval(() => {
      const particlesElement = document.getElementById("particles-js");
      if (particlesElement) {
        window.particlesJS("particles-js", {
          particles: {
            number: { value: 80, density: { enable: true, value_area: 800 } },
            color: { value: "#00ffff" },
            shape: { type: "circle" },
            opacity: { value: 0.5 },
            size: { value: 3 },
            line_linked: { enable: true, color: "#00ffff", opacity: 0.4 },
            move: { enable: true, speed: 2 },
          },
          interactivity: {
            events: {
              onhover: { enable: true, mode: "repulse" },
              onclick: { enable: true, mode: "push" },
            },
          },
          retina_detect: true,
        });
        clearInterval(interval);
      }
    }, 100);
  }, []);

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
    setError(""); // Clear error on input change
  };

  const handleRegister = async (e) => {
    e.preventDefault();
    const { name, email, password, accessCode } = form;

    if (!name || !email || !password || !accessCode) {
      setError("❌ All fields are required.");
      return;
    }

    if (accessCode !== ADMIN_CODE) {
      setError("❌ Invalid access code.");
      return;
    }

    try {
      const res = await axios.post(
        "https://api-fable-forest.onrender.com/api/auth/register",
        { name, email, password }
      );
      localStorage.setItem("token", res.data.token);
      window.location.href = "/user";
    } catch (err) {
      setError(err.response?.data?.message || "❌ Registration failed. Try again.");
    }
  };

  return (
    <div style={{
      position: "relative",
      width: "100vw",
      height: "100vh",
      overflow: "hidden",
      backgroundColor: "#000",
      fontFamily: "'Orbitron', sans-serif"
    }}>
      <div id="particles-js" style={{
        position: "absolute",
        width: "100%",
        height: "100%",
        zIndex: 0
      }}></div>

      <div style={{
        position: "relative",
        zIndex: 1,
        display: "flex",
        justifyContent: "center",
        marginTop: "8vh"
      }}>
        <form onSubmit={handleRegister} style={{
          width: "100%",
          maxWidth: "320px",
          padding: "24px",
          borderRadius: "10px",
          border: "2px solid #00ffff",
          backgroundColor: "rgba(0, 0, 0, 0.5)",
          boxShadow: "0 0 15px #00ffff",
          backdropFilter: "blur(4px)",
          color: "#fff"
        }}>
          <h2 style={{
            fontSize: "24px",
            color: "#00ffff",
            marginBottom: "14px",
            textAlign: "center",
            textShadow: "0 0 6px #00ffff"
          }}>REGISTER</h2>

          {["name", "email", "password", "accessCode"].map((field) => (
            <div key={field} style={{ marginBottom: "16px" }}>
              <label style={{
                display: "block",
                marginBottom: "5px",
                fontSize: "13px",
                color: "#ccc"
              }}>
                {field === "name" ? "Full Name" :
                 field === "email" ? "Email Address" :
                 field === "password" ? "Password" : "Access Code"}
              </label>
              <input
                type={field === "password" ? "password" : "text"}
                name={field}
                value={form[field]}
                onChange={handleChange}
                required
                style={{
                  width: "100%",
                  padding: "8px 12px",
                  fontSize: "13px",
                  backgroundColor: "transparent",
                  border: "1px solid #00ffff",
                  borderRadius: "6px",
                  color: "#fff",
                  outline: "none",
                  boxShadow: "0 0 6px #00ffff"
                }}
              />
            </div>
          ))}

          {error && (
            <div style={{
              marginBottom: "12px",
              color: "#ff0066",
              fontSize: "13px",
              textAlign: "center",
              fontWeight: "bold",
              textShadow: "0 0 4px #ff0066"
            }}>
              {error}
            </div>
          )}

          <button type="submit" style={{
            width: "100%",
            padding: "10px",
            fontSize: "14px",
            backgroundColor: "transparent",
            border: "1px solid #00ffff",
            color: "#00ffff",
            borderRadius: "6px",
            cursor: "pointer",
            boxShadow: "0 0 8px #00ffff"
          }}>Sign Up</button>

          <p style={{ marginTop: "12px", fontSize: "12px", color: "#ccc", textAlign: "center" }}>
            Already have an account? <a href="/login" style={{
              color: "#ff00ff",
              textDecoration: "none",
              fontWeight: "bold",
              textShadow: "0 0 5px #ff00ff"
            }}>Login here</a>
          </p>
        </form>
      </div>
    </div>
  );
};

export default Register;
