import React, { useEffect, useState } from "react";
import axios from "axios";

const User = () => {
  const [profile, setProfile] = useState(null);
  const [loading, setLoading] = useState(true);

  const fetchProfile = async () => {
    const token = localStorage.getItem("token");
    if (!token) {
      alert("You need to login first.");
      setLoading(false);
      return;
    }

    try {
      // Decode token to get user info (if you stored user info in localStorage)
      const userInfo = JSON.parse(localStorage.getItem("user"));
      const { data } = await axios.get("http://localhost:5000/api/userbooks/saved", {
        headers: { Authorization: `Bearer ${token}` },
      });

      setProfile({
        name: userInfo?.name || "Your Name",
        email: userInfo?.email || "your@email.com",
        purchasedBooks: data,
      });
    } catch (err) {
      alert("Failed to load profile.");
    }
    setLoading(false);
  };

  const handleRemove = async (bookId) => {
    if (!window.confirm("Are you sure you want to remove this book?")) return;

    try {
      const token = localStorage.getItem("token");
      await axios.delete(`http://localhost:5000/api/userbooks/remove`, {
        headers: { Authorization: `Bearer ${token}` },
        data: { id: bookId },
      });
      fetchProfile(); // Refresh profile data after removal
    } catch (err) {
      alert("Failed to remove the book.");
    }
  };

  useEffect(() => {
    fetchProfile();
  }, []);

  if (loading) return <p>Loading profile...</p>;
  if (!profile) return <p>No profile data available. Please login again.</p>;

  return (
    <div className="user-profile" style={{ padding: "40px" }}>
      <div
        className="profile-header"
        style={{
          display: "flex",
          alignItems: "center",
          marginBottom: "40px",
        }}
      >
        <img
          src="https://i.pravatar.cc/120"
          alt="Profile"
          style={{
            borderRadius: "50%",
            marginRight: "20px",
            border: "3px solid #ccc",
          }}
        />
        <div>
          <h2 style={{ margin: 0 }}>{profile.name}</h2>
          <p style={{ color: "#777" }}>{profile.email}</p>
        </div>
      </div>

      <h3 style={{ marginBottom: "20px" }}>Your Purchased Books</h3>

      {profile.purchasedBooks.length === 0 ? (
        <p>You haven't purchased any books yet.</p>
      ) : (
        <div
          className="books-grid"
          style={{ display: "flex", gap: 20, flexWrap: "wrap" }}
        >
          {profile.purchasedBooks.map((book) => (
            <div
              key={book._id || book.id}
              className="book-card"
              style={{
                width: 220,
                padding: 10,
                borderRadius: 12,
                boxShadow: "0 4px 10px rgba(0,0,0,0.1)",
                background: "#fff",
                textAlign: "center",
              }}
            >
              <img
                src={book.image}
                alt={book.title}
                style={{
                  width: "100%",
                  height: 280,
                  objectFit: "cover",
                  borderRadius: 10,
                  marginBottom: 10,
                }}
              />
              <h4 style={{ margin: "6px 0" }}>{book.title}</h4>
              <p
                style={{
                  fontStyle: "italic",
                  color: "#555",
                  marginBottom: 10,
                }}
              >
                {book.author}
              </p>
              <button
                onClick={() => handleRemove(book._id || book.id)}
                className="delete-btn"
                style={{
                  backgroundColor: "#e74c3c",
                  color: "#fff",
                  border: "none",
                  padding: "10px 20px",
                  borderRadius: 30,
                  cursor: "pointer",
                }}
              >
                Remove
              </button>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default User;
