import React, { useState, useEffect } from 'react';
import axios from 'axios';

// Example: a list of books with _id added
const books = [
  { _id: '1', title: 'Book One', author: 'Author A', image: 'https://placehold.co/200x280' },
  { _id: '2', title: 'Book Two', author: 'Author B', image: 'https://placehold.co/200x280' },
  // ... add 40-50 books
];

const BookList = () => {
  const token = localStorage.getItem('token');
  const [purchasedBooks, setPurchasedBooks] = useState([]);

  // Optionally, fetch purchased books on load to disable already purchased or just show alert on buy

  const handleBuy = async (book) => {
    if (!token) {
      alert('Please login to buy books');
      return;
    }

    const confirmPurchase = window.confirm(`Do you want to buy "${book.title}"?`);
    if (!confirmPurchase) return;

    try {
      await axios.post(
        'http://localhost:5000/api/books/buy',
        { bookId: book._id },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      alert('Book purchased!');
      setPurchasedBooks((prev) => [...prev, book._id]);
    } catch (error) {
      alert('Failed to purchase book.');
    }
  };

  return (
    <div className="book-list">
      <h2>Available Books</h2>
      <div className="book-grid" style={{ display: 'flex', gap: 20, flexWrap: 'wrap' }}>
        {books.map((book) => (
          <div key={book._id} className="book-card" style={{ width: 220 }}>
            <img src={book.image} alt={book.title} style={{ width: '100%', height: '280px', objectFit: 'cover' }} />
            <h4>{book.title}</h4>
            <p>{book.author}</p>
            <button onClick={() => handleBuy(book)} disabled={purchasedBooks.includes(book._id)}>
              {purchasedBooks.includes(book._id) ? 'Purchased' : 'Buy'}
            </button>
          </div>
        ))}
      </div>
    </div>
  );
};

export default BookList;
