import React, { useState } from 'react';
import '../styles.css';
import axios from 'axios';

const books = [
  // ... your existing array of books ...
];

const BookList = () => {
  const [search, setSearch] = useState('');

  const filteredBooks = books.filter(book =>
    book.title.toLowerCase().startsWith(search.toLowerCase())
  );

  const handleBuy = async (book) => {
    const token = localStorage.getItem('token');
    if (!token) {
      alert('Please login to buy books.');
      return;
    }
    if (!window.confirm(`Do you want to buy "${book.title}"?`)) return;

    try {
      await axios.post('http://localhost:5000/api/userbooks/save', { book }, {
        headers: { Authorization: `Bearer ${token}` }
      });
      alert(`"${book.title}" has been added to your profile.`);
    } catch (err) {
      alert(err.response?.data?.message || 'Failed to save book.');
    }
  };

  return (
    <div className="booklist-container">
      <h2>Library Book Collection</h2>

      <input
        type="text"
        placeholder="Search by first letter..."
        className="search-bar"
        value={search}
        onChange={(e) => setSearch(e.target.value)}
      />

      <div className="booklist-grid">
        {filteredBooks.map((book, index) => (
          <div className="booklist-card" key={index}>
            <img src={book.image} alt={book.title} className="booklist-image" />
            <div className="booklist-info">
              <h3>{book.title}</h3>
              <p className="author">by {book.author}</p>
              <div className="genre-tag">{book.genre}</div>
              <div className="price-rating">
                <div className="price">{book.price}</div>
                <div className="rating">
                  {'★'.repeat(Math.floor(book.rating))}{' '}
                  <span className="star empty">
                    {'☆'.repeat(5 - Math.floor(book.rating))}
                  </span>
                </div>
              </div>
              <button className="buy-btn" onClick={() => handleBuy(book)}>Buy Now</button>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default BookList;
