import React, { useState, useEffect } from 'react';
import axios from 'axios';

const AdminDashboard = () => {
  const [admin, setAdmin] = useState(null);
  const [users, setUsers] = useState([]);
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');

  const token = localStorage.getItem('adminToken');

  const fetchUsers = async () => {
    try {
      const res = await axios.get('/api/admin/users', {
        headers: { Authorization: `Bearer ${token}` },
      });
      setUsers(res.data);
    } catch (err) {
      console.error('Error fetching users:', err);
    }
  };

  const handleLogin = async (e) => {
    e.preventDefault();
    try {
      const res = await axios.post('/api/admin/login', { email, password });
      localStorage.setItem('adminToken', res.data.token);
      setAdmin(res.data.admin);
    } catch (err) {
      alert('Invalid admin credentials');
    }
  };

  const handleDeleteUser = async (id) => {
    try {
      await axios.delete(`/api/admin/users/${id}`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      setUsers(users.filter((u) => u._id !== id));
    } catch (err) {
      console.error('Error deleting user:', err);
    }
  };

  useEffect(() => {
    if (token) {
      axios
        .get('/api/admin/me', {
          headers: { Authorization: `Bearer ${token}` },
        })
        .then((res) => {
          setAdmin(res.data.admin);
          fetchUsers();
        })
        .catch(() => {
          localStorage.removeItem('adminToken');
        });
    }
  }, [token]);

  if (!admin) {
    return (
      <form onSubmit={handleLogin}>
        <h2>Admin Login</h2>
        <input
          type="email"
          placeholder="Admin Email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          required
        />
        <input
          type="password"
          placeholder="Admin Password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          required
        />
        <button type="submit">Login</button>
      </form>
    );
  }

  return (
    <div className="admin-dashboard">
      <h2>Welcome, {admin.name}</h2>
      <p>Email: {admin.email}</p>

      <div className="admin-analytics">
        <h3>üìä Dashboard Analytics</h3>
        <p><strong>Total Registered Users:</strong> {users.length}</p>
        {/* You can add more analytics here like:
          - Total books sold
          - Most active user
          - Most purchased book
        */}
      </div>

      <h3>üë• Registered Users</h3>
      <ul>
        {users.map((user) => (
          <li key={user._id}>
            {user.name} ({user.email})
            <button onClick={() => handleDeleteUser(user._id)}>‚ùå Delete</button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default AdminDashboard;
