import React, { useEffect, useState } from "react";
import axios from "axios";

const User = () => {
  const [profile, setProfile] = useState(null);

  const fetchProfile = async () => {
    const token = localStorage.getItem("token");
    try {
      const { data } = await axios.get("http://localhost:5000/api/user/profile", {
        headers: { Authorization: `Bearer ${token}` },
      });
      setProfile(data);
    } catch (err) {
      alert("Failed to load profile");
    }
  };

  const handleRemove = async (bookId) => {
    const confirmRemove = window.confirm("Remove this book?");
    if (!confirmRemove) return;

    try {
      const token = localStorage.getItem("token");
      await axios.delete(`http://localhost:5000/api/user/removeBook/${bookId}`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      fetchProfile(); // refresh
    } catch (err) {
      alert("Error removing book");
    }
  };

  useEffect(() => {
    fetchProfile();
  }, []);

  return (
    <div className="user-profile">
      <h2>{profile?.name}'s Purchased Books</h2>
      <div className="books-grid">
        {profile?.purchasedBooks?.map((book) => (
          <div key={book._id} className="book-card">
            <img src={book.image} alt={book.title} />
            <h4>{book.title}</h4>
            <p>{book.author}</p>
            <button onClick={() => handleRemove(book._id)}>Remove</button>
          </div>
        ))}
      </div>
    </div>
  );
};

export default User;
