import React, { useEffect, useState } from "react";
import axios from "axios";

const User = () => {
  const [profile, setProfile] = useState(null);
  const [loading, setLoading] = useState(true);
  const [userInfo, setUserInfo] = useState({ name: "User", email: "" });

  const fetchProfile = async () => {
    const token = localStorage.getItem("token");
    const storedUser = JSON.parse(localStorage.getItem("user"));

    if (!token) {
      alert("You need to login first.");
      setLoading(false);
      return;
    }

    if (storedUser) {
      setUserInfo({
        name: storedUser.name || "User",
        email: storedUser.email || "",
      });
    }

    try {
      const { data } = await axios.get("http://localhost:5000/api/userbooks/saved", {
        headers: { Authorization: `Bearer ${token}` },
      });
      setProfile({ purchasedBooks: data });
    } catch (err) {
      alert("Failed to load profile.");
    }
    setLoading(false);
  };

  const handleRemove = async (bookId) => {
    if (!window.confirm("Are you sure you want to remove this book?")) return;

    try {
      const token = localStorage.getItem("token");
      await axios.delete(`http://localhost:5000/api/userbooks/remove`, {
        headers: { Authorization: `Bearer ${token}` },
        data: { id: bookId },
      });
      fetchProfile();
    } catch (err) {
      alert("Failed to remove the book.");
    }
  };

  const handleLogout = () => {
    localStorage.removeItem("token");
    localStorage.removeItem("user");
    window.location.href = "/login";
  };

  useEffect(() => {
    fetchProfile();
  }, []);

  if (loading) return <p>Loading profile...</p>;
  if (!profile) return <p>No profile data available. Please login again.</p>;

  return (
    <div style={{ maxWidth: 1200, margin: "auto", padding: 20 }}>
      {/* Profile Header */}
      <div
        style={{
          display: "flex",
          alignItems: "center",
          gap: 20,
          background: "#f5f5f5",
          padding: 20,
          borderRadius: 12,
          marginBottom: 30,
        }}
      >
        <img
          src="https://cdn-icons-png.flaticon.com/512/847/847969.png"
          alt="User Avatar"
          style={{ width: 80, height: 80, borderRadius: "50%" }}
        />
        <div>
          <h2 style={{ marginBottom: 5 }}>{userInfo.name}</h2>
          <p style={{ color: "#555" }}>{userInfo.email}</p>
        </div>
        <button
          onClick={handleLogout}
          style={{
            marginLeft: "auto",
            backgroundColor: "#333",
            color: "#fff",
            padding: "10px 20px",
            borderRadius: 25,
            border: "none",
            cursor: "pointer",
          }}
        >
          Logout
        </button>
      </div>

      {/* Purchased Books */}
      <h3 style={{ marginBottom: 15 }}>ðŸ“š Your Purchased Books</h3>

      {profile.purchasedBooks.length === 0 ? (
        <p>You haven't purchased any books yet.</p>
      ) : (
        <div
          className="books-grid"
          style={{
            display: "grid",
            gridTemplateColumns: "repeat(auto-fit, minmax(220px, 1fr))",
            gap: 20,
          }}
        >
          {profile.purchasedBooks.map((book) => (
            <div
              key={book._id || book.id}
              style={{
                padding: 10,
                borderRadius: 12,
                boxShadow: "0 4px 10px rgba(0,0,0,0.1)",
                background: "#fff",
                textAlign: "center",
              }}
            >
              <img
                src={book.image}
                alt={book.title}
                style={{
                  width: "100%",
                  height: 280,
                  objectFit: "cover",
                  borderRadius: 10,
                  marginBottom: 10,
                }}
              />
              <h4 style={{ margin: "6px 0" }}>{book.title}</h4>
              <p
                style={{
                  fontStyle: "italic",
                  color: "#555",
                  marginBottom: 10,
                }}
              >
                {book.author}
              </p>
              <button
                onClick={() => handleRemove(book._id || book.id)}
                style={{
                  backgroundColor: "#e74c3c",
                  color: "#fff",
                  border: "none",
                  padding: "8px 18px",
                  borderRadius: 30,
                  cursor: "pointer",
                }}
              >
                Remove
              </button>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default User;
