import React from 'react';

// Sample book data (you can replace this with real API data later)
const books = [
  { id: 1, title: "The Enchanted Oak", author: "A. Storyteller" },
  { id: 2, title: "Mysteries of the Forest", author: "B. Writer" },
  { id: 3, title: "Tales of the River", author: "C. Narrator" },
  { id: 4, title: "Legends of the Moon", author: "D. Author" },
  { id: 5, title: "Whispers in the Wind", author: "E. Poet" },
  { id: 6, title: "Chronicles of the Night", author: "F. Scribe" },
];

const Home = () => {
  return (
    <div className="home-container">
      <h2>Welcome to the Fable Forest Library ðŸ“–</h2>
      <p>Login or register to continue.</p>

      <h3>Featured Books</h3>
      <div className="books-scroll-container">
        {books.map((book) => (
          <div key={book.id} className="book-card">
            <h4>{book.title}</h4>
            <p><em>by {book.author}</em></p>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Home;
