import React from 'react';
import { Link, useNavigate } from 'react-router-dom';

const Navbar = () => {
  const token = localStorage.getItem("token");
  const role = localStorage.getItem("userRole"); // get role from localStorage
  const navigate = useNavigate();

  const handleLogout = () => {
    localStorage.removeItem("token");
    localStorage.removeItem("userRole");
    localStorage.removeItem("userName");
    alert("You have been logged out.");
    navigate('/login');
  };

  return (
    <nav className="navbar">
      <div className="navbar-title">
        <h2>üìö Library Management System</h2>
      </div>
      <div className="nav-links">
        <Link to="/">üè† Home</Link>
        <Link to="/booklist">üõçÔ∏è Books</Link>

        {token ? (
          <>
            {role === "admin" && <Link to="/admin">üõ†Ô∏è Admin Dashboard</Link>}
            {role === "user" && <Link to="/user">üë§ Profile</Link>}
            <button onClick={handleLogout} className="logout-btn">üö™ Logout</button>
          </>
        ) : (
          <>
            <Link to="/login">üîê Login</Link>
            <Link to="/register">üìù Register</Link>
          </>
        )}
      </div>
    </nav>
  );
};

export default Navbar;
