import React from 'react';
import { FaFacebookF, FaInstagram, FaTwitter } from 'react-icons/fa';

// Genre data with emojis or icons (optional)
const genres = [
  { name: 'Horror', emoji: 'ðŸ‘»' },
  { name: 'Comedy', emoji: 'ðŸ˜‚' },
  { name: 'Fiction', emoji: 'ðŸ“–' },
  { name: 'Fantasy', emoji: 'ðŸ§™â€â™‚ï¸' },
  { name: 'Romance', emoji: 'â¤ï¸' },
  { name: 'Sci-Fi', emoji: 'ðŸš€' },
  { name: 'Biography', emoji: 'ðŸ‘¤' },
  { name: 'Adventure', emoji: 'ðŸŒ' },
];

// Sample book data
const books = [
  {
    id: 1,
    title: "The Enchanted Oak",
    author: "A. Storyteller",
    image: "https://images.unsplash.com/photo-1524995997946-a1c2e315a42f?auto=format&fit=crop&w=200&q=80"
  },
  {
    id: 2,
    title: "Mysteries of the Forest",
    author: "B. Writer",
    image: "https://images.unsplash.com/photo-1507842217343-583bb7270b66?auto=format&fit=crop&w=200&q=80"
  },
  {
    id: 3,
    title: "Tales of the River",
    author: "C. Narrator",
    image: "https://images.unsplash.com/photo-1512820790803-83ca734da794?auto=format&fit=crop&w=200&q=80"
  },
  {
    id: 4,
    title: "Legends of the Moon",
    author: "D. Author",
    image: "https://images.unsplash.com/photo-1506744038136-46273834b3fb?auto=format&fit=crop&w=200&q=80"
  },
];

const Home = () => {
  return (
    <div className="home-container">
      <h2>Welcome to the Fable Forest Library ðŸ“–</h2>
      <p>Login or register to continue.</p>

      {/* Genre Section */}
      <h3>Explore Genres</h3>
      <div className="genre-card-container">
        {genres.map((genre, index) => (
          <div key={index} className="genre-card">
            <span className="genre-emoji">{genre.emoji}</span>
            <span className="genre-name">{genre.name}</span>
          </div>
        ))}
      </div>

      {/* Books Section */}
      <h3>Featured Books</h3>
      <div className="books-grid">
        {books.map((book) => (
          <div key={book.id} className="book-card">
            <img
              src={book.image}
              alt={`Cover of ${book.title}`}
              className="book-image"
            />
            <h4>{book.title}</h4>
            <p><em>by {book.author}</em></p>
          </div>
        ))}
      </div>

      {/* Footer Section */}
      <footer className="footer">
        <p>&copy; 2025 Fable Forest Library</p>
        <div className="social-icons">
          <a href="#" className="icon facebook" aria-label="Facebook"><FaFacebookF /></a>
          <a href="#" className="icon instagram" aria-label="Instagram"><FaInstagram /></a>
          <a href="#" className="icon twitter" aria-label="Twitter"><FaTwitter /></a>
        </div>
      </footer>
    </div>
  );
};

export default Home;
