import React, { useState } from 'react';
import axios from 'axios';

const books = [
  // Example book data; replace with your 40-50 books
  {
    _id: '1',
    title: 'Book Title 1',
    author: 'Author 1',
    image: 'https://example.com/book1.jpg',
  },
  {
    _id: '2',
    title: 'Book Title 2',
    author: 'Author 2',
    image: 'https://example.com/book2.jpg',
  },
  // Add more book objects here...
];

const BookList = () => {
  const token = localStorage.getItem('token');
  const [purchasedBooks, setPurchasedBooks] = useState([]);

  const handleBuy = async (book) => {
    if (!token) {
      alert('Please login to buy books');
      return;
    }

    const confirmBuy = window.confirm(`Do you want to buy "${book.title}"?`);
    if (!confirmBuy) return;

    try {
      await axios.post(
        'http://localhost:5000/api/books/buy',
        { book },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      alert('Book purchased!');
      setPurchasedBooks([...purchasedBooks, book]);
    } catch (err) {
      alert('Failed to purchase book');
    }
  };

  return (
    <div className="book-list">
      <h2>Available Books</h2>
      <div className="book-grid">
        {books.map((book) => (
          <div key={book._id} className="book-card">
            <img src={book.image} alt={book.title} />
            <h4>{book.title}</h4>
            <p>{book.author}</p>
            <button onClick={() => handleBuy(book)}>Buy</button>
          </div>
        ))}
      </div>
    </div>
  );
};

export default BookList;
