import React from 'react';

const books = {
  Horror: [
    {
      title: "Dracula",
      author: "Bram Stoker",
      image: "https://covers.openlibrary.org/b/id/8231996-L.jpg",
    },
    {
      title: "Frankenstein",
      author: "Mary Shelley",
      image: "https://covers.openlibrary.org/b/id/8228691-L.jpg",
    },
    {
      title: "The Shining",
      author: "Stephen King",
      image: "https://covers.openlibrary.org/b/id/8225637-L.jpg",
    },
  ],
  Comedy: [
    {
      title: "Good Omens",
      author: "Neil Gaiman & Terry Pratchett",
      image: "https://covers.openlibrary.org/b/id/6979861-L.jpg",
    },
    {
      title: "Bossypants",
      author: "Tina Fey",
      image: "https://covers.openlibrary.org/b/id/8225634-L.jpg",
    },
    {
      title: "Catch-22",
      author: "Joseph Heller",
      image: "https://covers.openlibrary.org/b/id/8231852-L.jpg",
    },
  ],
  Fiction: [
    {
      title: "The Alchemist",
      author: "Paulo Coelho",
      image: "https://covers.openlibrary.org/b/id/8167892-L.jpg",
    },
    {
      title: "1984",
      author: "George Orwell",
      image: "https://covers.openlibrary.org/b/id/7222246-L.jpg",
    },
    {
      title: "The Great Gatsby",
      author: "F. Scott Fitzgerald",
      image: "https://covers.openlibrary.org/b/id/8225630-L.jpg",
    },
  ],
};

const Home = () => {
  return (
    <div className="home-container">
      <h2>Welcome to the Fable Forest Library</h2>
      <p>Please login or register to explore the collection.</p>

      {Object.entries(books).map(([genre, bookList]) => (
        <section className="genre-section" key={genre}>
          <h3 className="genre-title">{genre}</h3>
          <div className="books-horizontal-scroll">
            {bookList.map((book, index) => (
              <div className="book-card" key={index}>
                <img
                  className="book-image"
                  src={book.image}
                  alt={book.title}
                  loading="lazy"
                />
                <h4>{book.title}</h4>
                <p>{book.author}</p>
              </div>
            ))}
          </div>
        </section>
      ))}

      <footer className="footer">
        <div className="footer-content">
          <p>Connect with us:</p>
          <div className="footer-icons">
            <a
              href="https://facebook.com"
              target="_blank"
              rel="noopener noreferrer"
              aria-label="Facebook"
            >
              <img
                src="https://cdn-icons-png.flaticon.com/512/733/733547.png"
                alt="Facebook"
              />
            </a>
            <a
              href="https://instagram.com"
              target="_blank"
              rel="noopener noreferrer"
              aria-label="Instagram"
            >
              <img
                src="https://cdn-icons-png.flaticon.com/512/733/733558.png"
                alt="Instagram"
              />
            </a>
            <a
              href="https://x.com"
              target="_blank"
              rel="noopener noreferrer"
              aria-label="X Twitter"
            >
              <img
                src="https://cdn-icons-png.flaticon.com/512/733/733579.png"
                alt="X Twitter"
              />
            </a>
          </div>
        </div>
        <div className="footer-bottom">
          <small>© {new Date().getFullYear()} Fable Forest Library. All rights reserved.</small>
        </div>
      </footer>
    </div>
  );
};

export default Home;
