import React, { useEffect, useState } from 'react';
import axios from 'axios';

const User = () => {
  const [user, setUser] = useState(null);
  const [books, setBooks] = useState([]);

  const token = localStorage.getItem('token');

  useEffect(() => {
    if (!token) return;

    const fetchProfile = async () => {
      try {
        const res = await axios.get('http://localhost:5000/api/user/profile', {
          headers: { Authorization: `Bearer ${token}` },
        });
        setUser(res.data.user);
        setBooks(res.data.books || []); // Adjust based on backend response
      } catch (err) {
        console.error(err);
        alert('Failed to load user data.');
      }
    };

    fetchProfile();
  }, [token]);

  const handleDelete = async (bookId) => {
    if (!window.confirm('Are you sure you want to delete this book?')) return;

    try {
      await axios.delete(`http://localhost:5000/api/books/delete/${bookId}`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      setBooks(books.filter((b) => b._id !== bookId));
      alert('Book removed from your profile.');
    } catch (err) {
      alert('Error deleting book');
    }
  };

  if (!user) return <p>Loading user info...</p>;

  return (
    <div className="user-dashboard">
      <h2>Welcome, {user.name}</h2>
      <p>Email: {user.email}</p>
      <h3>Your Books</h3>
      <div className="book-grid" style={{ display: 'flex', gap: 20, flexWrap: 'wrap' }}>
        {books.length > 0 ? (
          books.map((book) => (
            <div key={book._id} className="book-card" style={{ width: 220 }}>
              <img src={book.image} alt={book.title} style={{ width: '100%', height: '280px', objectFit: 'cover' }} />
              <h4>{book.title}</h4>
              <p>{book.author}</p>
              <button onClick={() => handleDelete(book._id)}>Delete</button>
            </div>
          ))
        ) : (
          <p>No books purchased yet.</p>
        )}
      </div>
    </div>
  );
};

export default User;
