import React, { useEffect, useState } from 'react';
import axios from 'axios';

const User = () => {
  const [user, setUser] = useState(null); // start with null
  const [books, setBooks] = useState([]);

  const token = localStorage.getItem('token');

  useEffect(() => {
    if (!token) return;

    const fetchProfile = async () => {
      try {
        const res = await axios.get('http://localhost:5000/api/user/profile', {
          headers: { Authorization: `Bearer ${token}` },
        });
        setUser(res.data.user || null); // fallback to null if missing
        setBooks(res.data.books || []);
      } catch (err) {
        console.error(err);
        alert('Failed to load user data.');
      }
    };

    fetchProfile();
  }, [token]);

  if (!user) {
    // Show loading or nothing until user data is fetched
    return <p>Loading user profile...</p>;
  }

  return (
    <div className="user-dashboard">
      <h2>Welcome, {user.name}</h2>
      <p>Email: {user.email}</p>
      <h3>Your Books</h3>
      <div className="book-grid">
        {books.length > 0 ? (
          books.map((book) => (
            <div key={book._id} className="book-card">
              <img src={book.image} alt={book.title} />
              <h4>{book.title}</h4>
              <p>{book.author}</p>
              <button
                onClick={async () => {
                  if (window.confirm('Are you sure you want to delete this book?')) {
                    try {
                      await axios.delete(`http://localhost:5000/api/books/delete/${book._id}`, {
                        headers: { Authorization: `Bearer ${token}` },
                      });
                      setBooks(books.filter((b) => b._id !== book._id));
                      alert('Book removed from your profile.');
                    } catch {
                      alert('Error deleting book');
                    }
                  }
                }}
              >
                Delete
              </button>
            </div>
          ))
        ) : (
          <p>No books purchased yet.</p>
        )}
      </div>
    </div>
  );
};

export default User;
