import React, { useEffect, useState } from 'react';
import axios from 'axios';

const User = () => {
  const [profile, setProfile] = useState({ username: '', email: '', savedBooks: [] });

  useEffect(() => {
    const fetchProfile = async () => {
      const token = localStorage.getItem('token');
      try {
        const res = await axios.get('http://localhost:5000/api/userbooks/saved', {
          headers: { Authorization: `Bearer ${token}` }
        });
        setProfile(res.data);
      } catch (err) {
        alert('Error fetching profile');
      }
    };
    fetchProfile();
  }, []);

  const handleDelete = async (bookId) => {
    const token = localStorage.getItem('token');
    if (!window.confirm('Are you sure you want to delete this book?')) return;

    try {
      await axios.delete(`http://localhost:5000/api/userbooks/delete/${bookId}`, {
        headers: { Authorization: `Bearer ${token}` }
      });
      setProfile((prev) => ({
        ...prev,
        savedBooks: prev.savedBooks.filter((book) => book._id !== bookId)
      }));
    } catch (err) {
      alert('Failed to delete book');
    }
  };

  return (
    <div style={{ padding: '20px' }}>
      <div style={{ backgroundColor: '#f3f3f3', padding: '20px', borderRadius: '10px', marginBottom: '20px' }}>
        <h2>Welcome, {profile.username}</h2>
        <p>Email: {profile.email}</p>
      </div>

      <h3>Saved Books:</h3>
      <div style={{ display: 'flex', flexWrap: 'wrap', gap: '20px' }}>
        {profile.savedBooks.map((book) => (
          <div key={book._id} style={{ width: '200px', border: '1px solid #ccc', borderRadius: '10px', padding: '10px' }}>
            <img src={book.image} alt={book.title} style={{ width: '100%', height: '250px', objectFit: 'cover' }} />
            <h4>{book.title}</h4>
            <p>{book.author}</p>
            <button
              onClick={() => handleDelete(book._id)}
              style={{
                backgroundColor: '#e74c3c',
                color: 'white',
                border: 'none',
                padding: '8px',
                borderRadius: '5px',
                cursor: 'pointer'
              }}
            >
              Delete
            </button>
          </div>
        ))}
      </div>
    </div>
  );
};

export default User;
