import React, { useEffect, useState } from 'react';
import axios from 'axios';

const BookList = () => {
  const [books, setBooks] = useState([]);

  const allBooks = [
    { title: "Atomic Habits", author: "James Clear", image: "https://covers.openlibrary.org/b/id/10546538-L.jpg" },
    { title: "1984", author: "George Orwell", image: "https://covers.openlibrary.org/b/id/7222246-L.jpg" },
    { title: "To Kill a Mockingbird", author: "Harper Lee", image: "https://covers.openlibrary.org/b/id/8228691-L.jpg" },
    { title: "The Great Gatsby", author: "F. Scott Fitzgerald", image: "https://covers.openlibrary.org/b/id/7222161-L.jpg" },
    // Add more books similarly (40-50)
  ];

  const handleBuy = async (book) => {
    try {
      const token = localStorage.getItem('token');
      await axios.post('http://localhost:5000/api/books/user', book, {
        headers: { Authorization: `Bearer ${token}` },
      });
      alert(`Purchased: ${book.title}`);
    } catch {
      alert('Login required to buy books');
    }
  };

  useEffect(() => {
    setBooks(allBooks);
  }, []);

  return (
    <div className="book-grid">
      {books.map((book, i) => (
        <div key={i} className="book-card">
          <img src={book.image} alt={book.title} />
          <h3>{book.title}</h3>
          <p>by {book.author}</p>
          <button onClick={() => handleBuy(book)}>Buy</button>
        </div>
      ))}
    </div>
  );
};

export default BookList;
