import React from 'react';
import './styles.css';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import Navbar from './components/Navbar';
import Home from './pages/Home';
import Login from './pages/Login';
import Register from './pages/Register';
import BookList from './pages/BookList';
import User from './pages/User';
import AdminDashboard from './pages/AdminDashboard';

// Helper to check auth and role from localStorage
const getAuth = () => {
  const token = localStorage.getItem('token');
  const userStr = localStorage.getItem('user');
  let user = null;
  if (userStr) {
    try {
      user = JSON.parse(userStr);
    } catch {}
  }
  return { token, user };
};

const RequireAuth = ({ children, role }) => {
  const { token, user } = getAuth();
  if (!token || !user) return <Navigate to="/login" replace />;

  if (role && user.role !== role) {
    // Redirect to their own dashboard if role doesn't match
    return user.role === 'admin' ? <Navigate to="/admin" replace /> : <Navigate to="/user" replace />;
  }

  return children;
};

function App() {
  return (
    <Router>
      <Navbar />
      <div className="container">
        <Routes>
          <Route path="/" element={<Home />} />
          <Route path="/booklist" element={<BookList />} />
          <Route path="/login" element={<Login />} />
          <Route path="/register" element={<Register />} />

          {/* User Profile Route, protected and role-based */}
          <Route
            path="/user"
            element={
              <RequireAuth role="user">
                <User />
              </RequireAuth>
            }
          />

          {/* Admin Dashboard Route, protected and role-based */}
          <Route
            path="/admin"
            element={
              <RequireAuth role="admin">
                <AdminDashboard />
              </RequireAuth>
            }
          />

          {/* Fallback */}
          <Route path="*" element={<Navigate to="/" />} />
        </Routes>
      </div>
    </Router>
  );
}

export default App;
