import React from 'react';

const allBooks = [
  {
    genre: "Horror",
    title: "Dracula",
    author: "Bram Stoker",
    image: "https://covers.openlibrary.org/b/id/8231996-L.jpg",
    price: 9.99,
    rating: 4.7,
  },
  {
    genre: "Horror",
    title: "Frankenstein",
    author: "Mary Shelley",
    image: "https://covers.openlibrary.org/b/id/8228691-L.jpg",
    price: 7.99,
    rating: 4.5,
  },
  {
    genre: "Horror",
    title: "It",
    author: "Stephen King",
    image: "https://covers.openlibrary.org/b/id/8231990-L.jpg",
    price: 11.99,
    rating: 4.8,
  },
  {
    genre: "Comedy",
    title: "Good Omens",
    author: "Neil Gaiman & Terry Pratchett",
    image: "https://covers.openlibrary.org/b/id/6979861-L.jpg",
    price: 12.5,
    rating: 4.6,
  },
  {
    genre: "Comedy",
    title: "Bossypants",
    author: "Tina Fey",
    image: "https://covers.openlibrary.org/b/id/8225634-L.jpg",
    price: 8.99,
    rating: 4.3,
  },
  {
    genre: "Comedy",
    title: "Catch-22",
    author: "Joseph Heller",
    image: "https://covers.openlibrary.org/b/id/8231852-L.jpg",
    price: 10.0,
    rating: 4.4,
  },
  {
    genre: "Fiction",
    title: "The Alchemist",
    author: "Paulo Coelho",
    image: "https://covers.openlibrary.org/b/id/8167892-L.jpg",
    price: 9.5,
    rating: 4.7,
  },
  {
    genre: "Fiction",
    title: "1984",
    author: "George Orwell",
    image: "https://covers.openlibrary.org/b/id/7222246-L.jpg",
    price: 7.5,
    rating: 4.9,
  },
  {
    genre: "Fiction",
    title: "The Great Gatsby",
    author: "F. Scott Fitzgerald",
    image: "https://covers.openlibrary.org/b/id/8225630-L.jpg",
    price: 8.75,
    rating: 4.2,
  },
];

const getStars = (rating) => {
  const stars = [];
  const fullStars = Math.floor(rating);
  const halfStar = rating % 1 >= 0.5;
  for (let i = 0; i < fullStars; i++) {
    stars.push(<span key={`full-${i}`} className="star">&#9733;</span>);
  }
  if (halfStar) {
    stars.push(<span key="half" className="star">&#189;</span>);
  }
  while (stars.length < 5) {
    stars.push(<span key={`empty-${stars.length}`} className="star empty">&#9733;</span>);
  }
  return stars;
};

const BookList = () => {
  return (
    <div className="booklist-container">
      <h2 style={{ fontSize: '2.8rem', marginBottom: '20px', color: '#1abc9c' }}>
        🛍️ Browse & Buy Your Next Favorite Book
      </h2>
      <p style={{ fontSize: '1.2rem', color: '#555', marginBottom: '40px' }}>
        Choose from bestsellers across genres — delivered to your bookshelf!
      </p>

      <div className="booklist-grid">
        {allBooks.map((book, idx) => (
          <div className="booklist-card" key={idx}>
            <img src={book.image} alt={book.title} className="booklist-image" loading="lazy" />
            <div className="booklist-info">
              <h3>{book.title}</h3>
              <p className="author">by {book.author}</p>
              <span className="genre-tag">{book.genre}</span>
              <div className="price-rating">
                <span className="price">${book.price.toFixed(2)}</span>
                <span className="rating">{getStars(book.rating)}</span>
              </div>
              <button
                className="buy-btn"
                onClick={() => alert(`🔔 ${book.title} — Purchase feature coming soon!`)}
              >
                Buy Now
              </button>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default BookList;
