import React from 'react';
import "../styles.css"; // Make sure this includes neon styles

const books = {
  Horror: [
    {
      title: "The Haunting of Hill House",
      author: "Shirley Jackson",
      image: "https://covers.openlibrary.org/b/id/8162601-L.jpg",
    },
    {
      title: "Pet Sematary",
      author: "Stephen King",
      image: "https://covers.openlibrary.org/b/id/8231654-L.jpg",
    },
    {
      title: "The Silent Patient",
      author: "Alex Michaelides",
      image: "https://covers.openlibrary.org/b/id/10543729-L.jpg",
    },
  ],
  Comedy: [
    {
      title: "Yes Please",
      author: "Amy Poehler",
      image: "https://covers.openlibrary.org/b/id/8231601-L.jpg",
    },
    {
      title: "Me Talk Pretty One Day",
      author: "David Sedaris",
      image: "https://covers.openlibrary.org/b/id/8231923-L.jpg",
    },
    {
      title: "Is Everyone Hanging Out Without Me?",
      author: "Mindy Kaling",
      image: "https://covers.openlibrary.org/b/id/10543820-L.jpg",
    },
  ],
  Fiction: [
    {
      title: "To Kill a Mockingbird",
      author: "Harper Lee",
      image: "https://covers.openlibrary.org/b/id/8226091-L.jpg",
    },
    {
      title: "The Book Thief",
      author: "Markus Zusak",
      image: "https://covers.openlibrary.org/b/id/8231627-L.jpg",
    },
    {
      title: "The Night Circus",
      author: "Erin Morgenstern",
      image: "https://covers.openlibrary.org/b/id/8231642-L.jpg",
    },
  ],
};

const Home = () => {
  return (
    <div className="home-container neon-bg">
      <h2 className="home-title neon-text">
        üå≤ Welcome to <span className="highlight">Fable Forest</span>
      </h2>
      <p className="home-subtitle">
        Explore genres, discover hidden gems, and lose yourself in a forest of timeless stories. ‚ú®
      </p>

      {Object.entries(books).map(([genre, bookList]) => (
        <section className="genre-section neon-box" key={genre}>
          <h2 className="genre-title neon-text">{genre} üìö</h2>
          <div className="books-scroll">
            {bookList.map((book, index) => (
              <div className="book-card neon-card" key={index}>
                <img className="book-image" src={book.image} alt={book.title} loading="lazy" />
                <h4 className="book-title">{book.title}</h4>
                <p className="book-author">{book.author}</p>
              </div>
            ))}
          </div>
        </section>
      ))}

      {/* üëá About Us Section */}
      <section className="about-us neon-box">
        <h2 className="neon-text">About Us üåü</h2>
        <p className="about-text">
          At <strong>Fable Forest</strong>, we believe every book is a portal ‚Äî not just to another world,
          but to another version of yourself. Our mission is to build a digital sanctuary where imagination
          thrives, genres collide, and readers connect. Whether you're into haunted mansions, sarcastic memoirs,
          or poetic fiction, we‚Äôve got a story waiting for you.
        </p>
        <blockquote className="about-quote">
          ‚ÄúBooks are mirrors: you only see in them what you already have inside you.‚Äù ‚Äî Carlos Ruiz Zaf√≥n
        </blockquote>
        <div className="genre-highlight">
          <span>üßü Horror</span>
          <span>üòÇ Comedy</span>
          <span>ü™Ñ Fiction</span>
        </div>
      </section>

      {/* Footer */}
      <footer className="footer neon-footer">
        <div className="footer-content">
          <p>üì¨ Connect with us:</p>
          <div className="footer-icons">
            <a href="https://facebook.com" target="_blank" rel="noopener noreferrer">
              <img src="https://cdn-icons-png.flaticon.com/512/145/145802.png" alt="Facebook" className="footer-icon-img" />
            </a>
            <a href="https://instagram.com" target="_blank" rel="noopener noreferrer">
              <img src="https://cdn-icons-png.flaticon.com/512/2111/2111463.png" alt="Instagram" className="footer-icon-img" />
            </a>
            <a href="https://x.com" target="_blank" rel="noopener noreferrer">
              <img src="https://cdn-icons-png.flaticon.com/512/5968/5968830.png" alt="X Twitter" className="footer-icon-img" />
            </a>
          </div>
        </div>
        <div className="footer-bottom">
          <small>¬© {new Date().getFullYear()} Fable Forest Library. Crafted with curiosity in Chattogram üåô</small>
        </div>
      </footer>
    </div>
  );
};

export default Home;
