import React from 'react';
import { Link, useNavigate } from 'react-router-dom';

const Navbar = () => {
  const token = localStorage.getItem('token');
  const user = JSON.parse(localStorage.getItem('user'));
  const navigate = useNavigate();

  const handleLogout = () => {
    localStorage.clear();
    alert('Logged out');
    navigate('/login');
  };

  return (
    <nav className="navbar">
      <div className="navbar-title">
        <h2>üìö Library System</h2>
      </div>
      <div className="nav-links">
        <Link to="/">üè† Home</Link>
        <Link to="/booklist">üìñ Books</Link>

        {token ? (
          <>
            {user?.isAdmin ? (
              <Link to="/admindashboard">üõ† Admin</Link>
            ) : (
              <Link to="/user">üë§ Profile</Link>
            )}
            <button onClick={handleLogout}>üö™ Logout</button>
          </>
        ) : (
          <>
            <Link to="/login">üîê Login</Link>
            <Link to="/register">üìù Register</Link>
          </>
        )}
      </div>
    </nav>
  );
};

export default Navbar;
