import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const User = () => {
  const [user, setUser] = useState(null);
  const [books, setBooks] = useState([]);
  const navigate = useNavigate();
  const token = localStorage.getItem('token');

  useEffect(() => {
    if (!token) {
      navigate('/login');
      return;
    }

    const fetchProfile = async () => {
      try {
        const res = await axios.get('http://localhost:5000/api/auth/profile', {
          headers: { Authorization: `Bearer ${token}` },
        });
        setUser(res.data);
      } catch (err) {
        console.error('Failed to fetch profile:', err);
        // token might be invalid/expired, force logout
        localStorage.clear();
        navigate('/login');
      }
    };

    const fetchBooks = async () => {
      try {
        const res = await axios.get('http://localhost:5000/api/books/mybooks', {
          headers: { Authorization: `Bearer ${token}` },
        });
        setBooks(res.data);
      } catch (err) {
        console.error('Failed to fetch books:', err);
      }
    };

    fetchProfile();
    fetchBooks();
  }, [token, navigate]);

  const handleDelete = async (index) => {
    try {
      await axios.delete(`http://localhost:5000/api/books/remove/${index}`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      const updatedBooks = [...books];
      updatedBooks.splice(index, 1);
      setBooks(updatedBooks);
    } catch (err) {
      console.error('Failed to delete book:', err);
    }
  };

  if (!user) return <p>Loading profile...</p>;

  return (
    <div style={{ padding: '20px' }}>
      <h2>Welcome, {user.name}</h2>
      <p>Email: {user.email}</p>

      <h3 style={{ marginTop: '30px' }}>Purchased Books:</h3>
      {books.length === 0 ? (
        <p>No books purchased yet.</p>
      ) : (
        <div style={{ display: 'flex', flexWrap: 'wrap', gap: '16px' }}>
          {books.map((book, index) => (
            <div
              key={index}
              style={{ border: '1px solid #ddd', padding: '10px', width: '200px' }}
            >
              <img
                src={book.image}
                alt={book.title}
                style={{ width: '100%', height: '250px', objectFit: 'cover' }}
              />
              <h4>{book.title}</h4>
              <p>Author: {book.author}</p>
              <p>Price: {book.price}</p>
              <button
                onClick={() => handleDelete(index)}
                style={{ backgroundColor: 'crimson', color: 'white', border: 'none', padding: '5px 10px' }}
              >
                Delete
              </button>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default User;
