import React, { useEffect, useState } from 'react';
import axios from 'axios';

const User = () => {
  const [user, setUser] = useState(null);
  const [books, setBooks] = useState([]);
  const token = localStorage.getItem('token');

  useEffect(() => {
    if (!token) return;

    // Fetch user profile
    const fetchUser = async () => {
      try {
        const res = await axios.get('http://localhost:5000/api/auth/profile', {
          headers: { Authorization: `Bearer ${token}` },
        });
        setUser(res.data);
      } catch (err) {
        console.error('Failed to load user profile:', err.response?.data?.message || err.message);
      }
    };

    // Fetch purchased books
    const fetchBooks = async () => {
      try {
        const res = await axios.get('http://localhost:5000/api/books/mybooks', {
          headers: { Authorization: `Bearer ${token}` },
        });
        setBooks(res.data || []);
      } catch (err) {
        console.error('Failed to load purchased books:', err.response?.data?.message || err.message);
      }
    };

    fetchUser();
    fetchBooks();
  }, [token]);

  const handleDelete = async (index) => {
    try {
      await axios.delete(`http://localhost:5000/api/books/remove/${index}`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      const newBooks = [...books];
      newBooks.splice(index, 1);
      setBooks(newBooks);
    } catch (err) {
      console.error('Error deleting book:', err.response?.data?.message || err.message);
    }
  };

  if (!user) {
    return <div style={{ padding: '2rem' }}>Loading user data...</div>;
  }

  return (
    <div style={{ padding: '2rem' }}>
      <h2>Welcome, {user.name}</h2>
      <p>Email: {user.email}</p>
      <h3>Your Purchased Books</h3>

      {books.length === 0 ? (
        <p>No books purchased yet.</p>
      ) : (
        <div style={{ display: 'flex', flexWrap: 'wrap', gap: '1rem' }}>
          {books.map((book, index) => (
            <div key={index} style={{
              border: '1px solid #ccc',
              borderRadius: '8px',
              padding: '1rem',
              width: '200px',
              textAlign: 'center'
            }}>
              <img src={book.image} alt={book.title} style={{ width: '100%', height: '250px', objectFit: 'cover' }} />
              <h4>{book.title}</h4>
              <p>{book.author}</p>
              <p>Price: {book.price}</p>
              <button onClick={() => handleDelete(index)} style={{
                backgroundColor: '#ff4d4d',
                color: 'white',
                border: 'none',
                padding: '0.5rem 1rem',
                borderRadius: '4px',
                cursor: 'pointer'
              }}>Delete</button>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default User;
