import React, { useEffect, useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

const User = () => {
  const [userBooks, setUserBooks] = useState([]);
  const navigate = useNavigate();

  // Check for login
  useEffect(() => {
    const token = localStorage.getItem("token");
    if (!token) {
      navigate("/login");
    } else {
      fetchUserBooks();
    }
  }, [navigate]);

  const fetchUserBooks = async () => {
    const token = localStorage.getItem("token");
    try {
      const response = await axios.get("http://localhost:5000/api/user/books", {
        headers: { Authorization: `Bearer ${token}` }
      });
      setUserBooks(response.data);
    } catch (error) {
      console.error("Error fetching user books:", error);
      alert("Could not load your books. Please try again.");
    }
  };

  const removeBook = async (bookId) => {
    try {
      await axios.delete(`http://localhost:5000/api/user/books/${bookId}`, {
        headers: { Authorization: `Bearer ${localStorage.getItem("token")}` }
      });
      setUserBooks(userBooks.filter((book) => book._id !== bookId));
    } catch (error) {
      console.error("Error removing book:", error);
      alert("Failed to remove book.");
    }
  };

  const handleLogout = () => {
    localStorage.removeItem("token");
    alert("Logged out successfully!");
    navigate("/login");
  };

  return (
    <div className="booklist-container">
      <h2>ðŸ“š Your Book Collection</h2>
      <button onClick={handleLogout} style={{ marginBottom: "20px" }}>
        Logout
      </button>

      {userBooks.length === 0 ? (
        <p>No books found in your collection.</p>
      ) : (
        <div className="booklist-grid">
          {userBooks.map((book) => (
            <div className="booklist-card" key={book._id}>
              <img
                src={book.image}
                alt={book.title}
                className="booklist-image"
              />
              <div className="booklist-info">
                <h3>{book.title}</h3>
                <p className="author">{book.author}</p>
                <div className="price-rating">
                  <span className="price">${book.price}</span>
                </div>
                <button
                  className="buy-btn"
                  onClick={() => removeBook(book._id)}
                >
                  Remove
                </button>
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default User;
