import React from 'react';
import './styles.css';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import Navbar from './components/Navbar';
import Home from './pages/Home';
import Login from './pages/Login';
import Register from './pages/Register';
import BookList from './pages/BookList';
import User from './pages/User';
import AdminDashboard from './pages/AdminDashboard'; // create this page

function App() {
  const token = localStorage.getItem("token");
  const role = localStorage.getItem("role"); // 'user' or 'admin'

  const isAuthenticated = !!token;
  const isAdmin = isAuthenticated && role === 'admin';
  const isUser = isAuthenticated && role === 'user';

  return (
    <Router>
      <Navbar />
      <div className="container">
        <Routes>
          <Route path="/" element={<Home />} />
          <Route path="/booklist" element={<BookList />} />
          <Route path="/login" element={<Login />} />
          <Route path="/register" element={<Register />} />

          {/* Protected routes */}
          <Route
            path="/user"
            element={isUser ? <User /> : <Navigate to="/login" />}
          />

          <Route
            path="/admin"
            element={isAdmin ? <AdminDashboard /> : <Navigate to="/login" />}
          />

          {/* Redirect logged-in users to their appropriate page */}
          <Route
            path="/dashboard"
            element={
              isAdmin ? <Navigate to="/admin" /> :
              isUser ? <Navigate to="/user" /> :
              <Navigate to="/login" />
            }
          />

          {/* Catch-all redirect to home or login */}
          <Route
            path="*"
            element={isAuthenticated ? <Navigate to="/dashboard" /> : <Navigate to="/login" />}
          />
        </Routes>
      </div>
    </Router>
  );
}

export default App;
