import React, { useEffect, useState } from 'react';
import axios from 'axios';

const User = () => {
  const [books, setBooks] = useState([]);
  const user = JSON.parse(localStorage.getItem('user'));

  const fetchPurchasedBooks = async () => {
    try {
      const token = localStorage.getItem('token');
      const res = await axios.get('http://localhost:5000/api/books/user', {
        headers: { Authorization: `Bearer ${token}` },
      });
      setBooks(res.data);
    } catch (err) {
      alert('Failed to load purchased books');
    }
  };

  const deleteBook = async (bookId) => {
    if (!window.confirm('Delete this book?')) return;
    try {
      const token = localStorage.getItem('token');
      await axios.delete(`http://localhost:5000/api/books/user/${bookId}`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      fetchPurchasedBooks();
    } catch {
      alert('Failed to delete');
    }
  };

  useEffect(() => {
    fetchPurchasedBooks();
  }, []);

  return (
    <div>
      <h2>Welcome, {user?.name} üìö</h2>
      <div className="book-grid">
        {books.map((book) => (
          <div key={book._id} className="book-card">
            <img src={book.image} alt={book.title} />
            <h3>{book.title}</h3>
            <p>by {book.author}</p>
            <button onClick={() => deleteBook(book._id)}>‚ùå Remove</button>
          </div>
        ))}
      </div>
    </div>
  );
};

export default User;
