// src/pages/AdminDashboard.jsx
import React, { useState, useEffect, useCallback } from 'react';
import axios from 'axios';
import { motion } from 'framer-motion';

const AdminDashboard = () => {
  const [admin, setAdmin] = useState(null);
  const [users, setUsers] = useState([]);
  const [books, setBooks] = useState([]);
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [loading, setLoading] = useState(true);

  const [title, setTitle] = useState('');
  const [author, setAuthor] = useState('');
  const [genre, setGenre] = useState('');
  const [price, setPrice] = useState('');
  const [image, setImage] = useState(null);
  const [pdf, setPdf] = useState(null);

  const token = localStorage.getItem('adminToken');

  const fetchUsers = useCallback(async () => {
    try {
      const res = await axios.get('/api/admin/users', {
        headers: { Authorization: `Bearer ${token}` },
      });
      setUsers(res.data);
    } catch (err) {
      console.error('Error fetching users:', err);
    }
  }, [token]);

  const fetchBooks = useCallback(async () => {
    try {
      const res = await axios.get('/api/books');
      setBooks(res.data);
    } catch (err) {
      console.error('Error fetching books:', err);
    }
  }, []);

  const handleLogin = async (e) => {
    e.preventDefault();
    try {
      const res = await axios.post('/api/admin/login', { email, password });
      localStorage.setItem('adminToken', res.data.token);
      setAdmin(res.data.admin);
      fetchUsers();
      fetchBooks();
    } catch (err) {
      alert('Invalid admin credentials');
    }
  };

  const handleDeleteUser = async (id) => {
    if (!window.confirm('Are you sure you want to delete this user?')) return;
    try {
      await axios.delete(`/api/admin/users/${id}`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      setUsers(users.filter(u => u._id !== id));
    } catch (err) {
      console.error('Error deleting user:', err);
    }
  };

  const handleCreateBook = async (e) => {
    e.preventDefault();
    if (!image || !pdf) return alert('Please upload both image and PDF');

    const formData = new FormData();
    formData.append('title', title);
    formData.append('author', author);
    formData.append('genre', genre);
    formData.append('price', price);
    formData.append('image', image);
    formData.append('pdf', pdf);

    try {
      const res = await axios.post('/api/admin/books', formData, {
        headers: { 'Content-Type': 'multipart/form-data', Authorization: `Bearer ${token}` },
      });
      alert(`Book "${res.data.title}" created successfully!`);
      setTitle(''); setAuthor(''); setGenre(''); setPrice('');
      setImage(null); setPdf(null);
      fetchBooks();
    } catch (err) {
      console.error('Error creating book:', err.response || err);
      alert(err.response?.data?.message || 'Failed to create book');
    }
  };

  const handleDeleteBook = async (id) => {
    if (!window.confirm('Are you sure you want to delete this book?')) return;
    try {
      await axios.delete(`/api/admin/books/${id}`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      alert('Book deleted successfully');
      setBooks(books.filter(b => b._id !== id));
    } catch (err) {
      console.error('Error deleting book:', err);
      alert('Failed to delete book');
    }
  };

  useEffect(() => {
    const fetchAdmin = async () => {
      if (!token) { setLoading(false); return; }
      try {
        const res = await axios.get('/api/admin/me', { headers: { Authorization: `Bearer ${token}` } });
        setAdmin(res.data.admin);
        fetchUsers();
        fetchBooks();
      } catch (err) {
        console.error('Admin auth failed:', err);
        localStorage.removeItem('adminToken');
      } finally { setLoading(false); }
    };
    fetchAdmin();
  }, [fetchUsers, fetchBooks, token]);

  if (loading) return <p className="text-white text-center mt-20">Loading...</p>;

  if (!admin) {
    return (
      <div className="min-h-screen flex items-center justify-center bg-gradient-to-r from-gray-900 to-black">
        <form onSubmit={handleLogin} className="bg-gray-800 p-8 rounded-xl shadow-lg w-96 space-y-4">
          <h2 className="text-3xl font-bold text-indigo-500 text-center">Admin Login</h2>
          <input type="email" placeholder="Email" value={email} onChange={e => setEmail(e.target.value)}
            className="w-full px-4 py-2 rounded-lg bg-gray-700 text-white focus:outline-none focus:ring-2 focus:ring-indigo-500" required />
          <input type="password" placeholder="Password" value={password} onChange={e => setPassword(e.target.value)}
            className="w-full px-4 py-2 rounded-lg bg-gray-700 text-white focus:outline-none focus:ring-2 focus:ring-indigo-500" required />
          <button type="submit" className="w-full py-2 bg-indigo-500 hover:bg-indigo-600 font-bold rounded-lg transition duration-300">Login</button>
        </form>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gradient-to-r from-gray-900 via-gray-800 to-black p-8 text-white">
      <motion.h2 initial={{ opacity: 0, y: -20 }} animate={{ opacity: 1, y: 0 }} transition={{ duration: 0.7 }}
        className="text-4xl font-bold text-indigo-500 mb-2">Welcome, {admin.name}</motion.h2>
      <p className="text-gray-300 mb-6">Email: {admin.email}</p>

      <div className="grid grid-cols-1 md:grid-cols-2 gap-6 mb-8">
        <div className="bg-gray-800 p-6 rounded-xl shadow-lg">
          <h3 className="text-xl font-bold text-indigo-400 mb-2">Registered Users</h3>
          <p className="text-gray-300 font-semibold">{users.length} users</p>
        </div>
        <div className="bg-gray-800 p-6 rounded-xl shadow-lg">
          <h3 className="text-xl font-bold text-indigo-400 mb-2">Created Books</h3>
          <p className="text-gray-300 font-semibold">{books.length} books</p>
        </div>
      </div>

      <div className="grid grid-cols-1 md:grid-cols-2 gap-8 mb-12">
        <div>
          <h3 className="text-2xl font-bold text-indigo-400 mb-4">📚 Create New Book</h3>
          <form onSubmit={handleCreateBook} className="bg-gray-800 p-6 rounded-xl shadow-lg space-y-4">
            <input type="text" placeholder="Title" value={title} onChange={e => setTitle(e.target.value)}
              className="w-full px-4 py-2 rounded-lg bg-gray-700 text-white focus:outline-none focus:ring-2 focus:ring-indigo-500" required />
            <input type="text" placeholder="Author" value={author} onChange={e => setAuthor(e.target.value)}
              className="w-full px-4 py-2 rounded-lg bg-gray-700 text-white focus:outline-none focus:ring-2 focus:ring-indigo-500" required />
            <input type="text" placeholder="Genre" value={genre} onChange={e => setGenre(e.target.value)}
              className="w-full px-4 py-2 rounded-lg bg-gray-700 text-white focus:outline-none focus:ring-2 focus:ring-indigo-500" required />
            <input type="number" placeholder="Price" value={price} onChange={e => setPrice(e.target.value)}
              className="w-full px-4 py-2 rounded-lg bg-gray-700 text-white focus:outline-none focus:ring-2 focus:ring-indigo-500" required />
            <input type="file" accept="image/*" onChange={e => setImage(e.target.files[0])} required className="text-white" />
            <input type="file" accept="application/pdf" onChange={e => setPdf(e.target.files[0])} required className="text-white" />
            <button type="submit" className="w-full py-2 bg-indigo-500 hover:bg-indigo-600 font-bold rounded-lg transition duration-300">Create Book</button>
          </form>
        </div>

        <div>
          <h3 className="text-2xl font-bold text-indigo-400 mb-4">👥 Users & Books</h3>
          <div className="mb-6">
            <h4 className="text-xl font-semibold text-indigo-300 mb-2">Registered Users</h4>
            <ul className="space-y-2 max-h-64 overflow-y-auto">
              {users.map(user => (
                <li key={user._id} className="bg-gray-700 p-3 rounded-lg flex justify-between items-center hover:bg-gray-600 transition">
                  {user.name} ({user.email})
                  <button onClick={() => handleDeleteUser(user._id)}
                    className="bg-red-600 hover:bg-red-700 text-white px-3 py-1 rounded-lg transition">Delete</button>
                </li>
              ))}
            </ul>
          </div>

          <div>
            <h4 className="text-xl font-semibold text-indigo-300 mb-2">Created Books</h4>
            <ul className="space-y-2 max-h-64 overflow-y-auto">
              {books.map(book => (
                <li key={book._id} className="bg-gray-700 p-3 rounded-lg flex justify-between items-center hover:bg-gray-600 transition">
                  {book.title} by {book.author}
                  <button onClick={() => handleDeleteBook(book._id)}
                    className="bg-red-600 hover:bg-red-700 text-white px-3 py-1 rounded-lg transition">Delete Book</button>
                </li>
              ))}
            </ul>
          </div>
        </div>
      </div>
    </div>
  );
};

export default AdminDashboard;
