import React, { useEffect, useState } from 'react';
import axios from 'axios';

const User = () => {
  const [user, setUser] = useState(null);
  const [error, setError] = useState('');

  const fetchProfile = async () => {
    try {
      const token = localStorage.getItem('token');
      const res = await axios.get('/api/user/profile', {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });
      setUser(res.data);
    } catch (err) {
      setError('Failed to fetch profile.');
    }
  };

  const handleRemove = async (bookId) => {
    try {
      const token = localStorage.getItem('token');
      await axios.delete(`/api/user/remove/${bookId}`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      fetchProfile(); // Refresh the user data
    } catch (err) {
      setError('Failed to remove book.');
    }
  };

  useEffect(() => {
    fetchProfile();
  }, []);

  if (!user) return <div className="user-dashboard">Loading user info...</div>;

  return (
    <div className="user-dashboard">
      <h2>üìò {user.name}'s Dashboard</h2>
      <p><strong>Email:</strong> {user.email}</p>

      <h3>Purchased Books</h3>
      {user.purchasedBooks.length === 0 ? (
        <p>No books purchased yet.</p>
      ) : (
        <div className="purchased-books">
          {user.purchasedBooks.map((book) => (
            <div key={book._id} className="purchased-book-card">
              <img src={book.image} alt={book.title} width="80" />
              <div>
                <h4>{book.title}</h4>
                <p>Author: {book.author}</p>
                <p>Genre: {book.genre}</p>
                <p>Price: ${book.price}</p>
                <p>Purchased At: {new Date(book.createdAt).toLocaleString()}</p>
              </div>
              <button onClick={() => handleRemove(book._id)}>‚ùå Remove</button>
            </div>
          ))}
        </div>
      )}
      {error && <p className="error">{error}</p>}
    </div>
  );
};

export default User;
