import React from 'react';

const books = {
  Horror: [
    {
      title: "Dracula",
      author: "Bram Stoker",
      image: "https://covers.openlibrary.org/b/id/8231996-L.jpg",
    },
    {
      title: "Frankenstein",
      author: "Mary Shelley",
      image: "https://covers.openlibrary.org/b/id/8228691-L.jpg",
    },
    {
      title: "It",
      author: "Stephen King",
      image: "https://covers.openlibrary.org/b/id/8231990-L.jpg",
    },
  ],
  Comedy: [
    {
      title: "Good Omens",
      author: "Neil Gaiman & Terry Pratchett",
      image: "https://covers.openlibrary.org/b/id/6979861-L.jpg",
    },
    {
      title: "Bossypants",
      author: "Tina Fey",
      image: "https://covers.openlibrary.org/b/id/8225634-L.jpg",
    },
    {
      title: "Catch-22",
      author: "Joseph Heller",
      image: "https://covers.openlibrary.org/b/id/8231852-L.jpg",
    },
  ],
  Fiction: [
    {
      title: "The Alchemist",
      author: "Paulo Coelho",
      image: "https://covers.openlibrary.org/b/id/8167892-L.jpg",
    },
    {
      title: "1984",
      author: "George Orwell",
      image: "https://covers.openlibrary.org/b/id/7222246-L.jpg",
    },
    {
      title: "The Great Gatsby",
      author: "F. Scott Fitzgerald",
      image: "https://covers.openlibrary.org/b/id/8225630-L.jpg",
    },
  ],
};

const Home = () => {
  return (
    <div className="home-container">
      <h1 className="site-title">ðŸ“š Welcome to Fable Forest</h1>
      <p className="subtitle">Discover your next favorite read. Login or register to begin your journey!</p>

      {Object.entries(books).map(([genre, bookList]) => (
        <section className="genre-section" key={genre}>
          <h2 className="genre-title">{genre} ðŸ“–</h2>
          <div className="books-scroll">
            {bookList.map((book, index) => (
              <div className="book-card" key={index}>
                <img
                  className="book-image"
                  src={book.image}
                  alt={book.title}
                  loading="lazy"
                />
                <h4>{book.title}</h4>
                <p>{book.author}</p>
              </div>
            ))}
          </div>
        </section>
      ))}

      <footer className="footer">
        <div className="footer-content">
          <p>ðŸ“¬ Connect with us:</p>
          <div className="footer-icons">
            <a href="https://facebook.com" target="_blank" rel="noopener noreferrer" aria-label="Facebook">
              <img src="https://cdn-icons-png.flaticon.com/512/145/145802.png" alt="Facebook" className="footer-icon-img" />
            </a>
            <a href="https://instagram.com" target="_blank" rel="noopener noreferrer" aria-label="Instagram">
              <img src="https://cdn-icons-png.flaticon.com/512/2111/2111463.png" alt="Instagram" className="footer-icon-img" />
            </a>
            <a href="https://x.com" target="_blank" rel="noopener noreferrer" aria-label="X Twitter">
              <img src="https://cdn-icons-png.flaticon.com/512/5968/5968830.png" alt="X Twitter" className="footer-icon-img" />
            </a>
          </div>
        </div>
        <div className="footer-bottom">
          <small>Â© {new Date().getFullYear()} Fable Forest Library. All rights reserved.</small>
        </div>
      </footer>
    </div>
  );
};

export default Home;
