import React, { useEffect, useRef, useState } from "react";
import axios from "axios";
import "../styles.css";

const User = () => {
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);
  const BACKEND_URL = "http://localhost:5000";
  const canvasRef = useRef(null);
  const animationRef = useRef(null);

  // Fetch user profile
  const fetchProfile = async () => {
    try {
      const token = localStorage.getItem("token");
      if (!token) {
        alert("No token found. Please login.");
        window.location.href = "/login";
        return;
      }

      const res = await axios.get(`${BACKEND_URL}/api/user/profile`, {
        headers: { Authorization: `Bearer ${token}` },
      });

      setUser(res.data);
      setLoading(false);
    } catch (err) {
      console.error(err.response?.data || err.message);
      alert("Session expired or unauthorized. Please login again.");
      localStorage.removeItem("token");
      window.location.href = "/login";
    }
  };

  // Remove purchased book
  const handleRemoveBook = async (bookId) => {
    try {
      const token = localStorage.getItem("token");
      await axios.delete(`${BACKEND_URL}/api/user/books/${bookId}`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      fetchProfile();
    } catch (err) {
      console.error(err.response?.data || err.message);
      alert("Failed to remove book.");
    }
  };

  // Read book
  const handleReadBook = async (bookId) => {
    try {
      const token = localStorage.getItem("token");
      const res = await axios.get(`${BACKEND_URL}/api/read/${bookId}`, {
        headers: { Authorization: `Bearer ${token}` },
      });

      if (res.data.pdfUrl) {
        const fullPdfUrl = `${BACKEND_URL}${res.data.pdfUrl}`;
        window.open(fullPdfUrl, "_blank");
      } else {
        alert("PDF not available for this book.");
      }
    } catch (err) {
      console.error(err.response?.data || err.message);
      alert(err.response?.data?.message || "Cannot read this book.");
    }
  };

  useEffect(() => {
    fetchProfile();
  }, []);

  // DNA Canvas Animation
  useEffect(() => {
    const canvas = canvasRef.current;
    if (!canvas) return;
    const ctx = canvas.getContext("2d");

    let width = canvas.width = window.innerWidth;
    let height = canvas.height = window.innerHeight;

    const strands = [];
    const numParticles = 80;
    const radius = 80;
    const speed = 0.02;

    for (let i = 0; i < numParticles; i++) {
      const t = (i / numParticles) * Math.PI * 4;
      strands.push({ angle: t, y: (i / numParticles) * height });
    }

    const draw = () => {
      ctx.clearRect(0, 0, width, height);

      for (let i = 0; i < strands.length; i++) {
        const p = strands[i];
        const x1 = width / 2 + radius * Math.sin(p.angle);
        const x2 = width / 2 + radius * Math.sin(p.angle + Math.PI);
        const y = p.y;

        // Strands particles
        ctx.beginPath();
        ctx.fillStyle = "#00ffff";
        ctx.arc(x1, y, 4, 0, Math.PI * 2);
        ctx.fill();

        ctx.beginPath();
        ctx.fillStyle = "#ff00ff";
        ctx.arc(x2, y, 4, 0, Math.PI * 2);
        ctx.fill();

        // Connect rungs
        ctx.beginPath();
        ctx.strokeStyle = "#00ffff";
        ctx.moveTo(x1, y);
        ctx.lineTo(x2, y);
        ctx.stroke();

        // Animate
        p.angle += speed;
        p.y += 0.5;
        if (p.y > height) p.y = 0;
      }

      animationRef.current = requestAnimationFrame(draw);
    };

    draw();

    const handleResize = () => {
      if (!canvas) return;
      width = canvas.width = window.innerWidth;
      height = canvas.height = window.innerHeight;
    };

    window.addEventListener("resize", handleResize);

    return () => {
      cancelAnimationFrame(animationRef.current);
      window.removeEventListener("resize", handleResize);
    };
  }, []);

  if (loading) return <div className="user-loading">Loading user info‚Ä¶</div>;

  return (
    <div className="user-dashboard">
      <canvas ref={canvasRef} className="dna-canvas"></canvas>
      <div className="user-container">
        <div className="user-info neon-box">
          <h2>üë§ Welcome, {user.name}</h2>
          <p>Email: {user.email}</p>
        </div>

        <div className="user-books neon-box">
          <h3>üìö Purchased Books</h3>
          {user.purchasedBooks.length === 0 ? (
            <p>No books purchased yet.</p>
          ) : (
            <ul className="book-list">
              {user.purchasedBooks.map((book) => (
                <li key={book._id} className="book-item neon-card">
                  <div className="book-details">
                    <h4>{book.title}</h4>
                    <p>Genre: {book.genre}</p>
                    <p>Price: ${book.price}</p>
                  </div>
                  <div className="book-actions">
                    <button onClick={() => handleRemoveBook(book._id)} className="delete-button neon-btn">
                      ‚ùå Remove
                    </button>
                    <button onClick={() => handleReadBook(book._id)} className="read-button neon-btn">
                      üìñ Read
                    </button>
                  </div>
                </li>
              ))}
            </ul>
          )}
        </div>
      </div>
    </div>
  );
};

export default User;
