import React, { useEffect, useState } from "react";
import axios from "axios";
import BookList from "./BookList"; // Reuse your BookList component

const User = () => {
  const [userBooks, setUserBooks] = useState([]);

  const fetchUserBooks = async () => {
    const token = localStorage.getItem("token");
    try {
      const response = await axios.get("http://localhost:5000/api/user/books", {
        headers: { Authorization: `Bearer ${token}` }
      });
      setUserBooks(response.data);
    } catch (error) {
      console.error("Error fetching user books:", error);
    }
  };

  const removeBook = async (bookId) => {
    try {
      await axios.delete(`http://localhost:5000/api/user/books/${bookId}`, {
        headers: { Authorization: `Bearer ${localStorage.getItem("token")}` }
      });
      setUserBooks(userBooks.filter((book) => book._id !== bookId));
    } catch (error) {
      console.error("Error removing book:", error);
    }
  };

  useEffect(() => {
    fetchUserBooks();
  }, []);

  return (
    <div className="booklist-container">
      <h2>Your Book Collection</h2>
      <div className="booklist-grid">
        {userBooks.map((book) => (
          <div className="booklist-card" key={book._id}>
            <img src={book.image} alt={book.title} className="booklist-image" />
            <div className="booklist-info">
              <h3>{book.title}</h3>
              <p className="author">{book.author}</p>
              <div className="price-rating">
                <span className="price">${book.price}</span>
              </div>
              <button className="buy-btn" onClick={() => removeBook(book._id)}>Remove</button>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default User;
