import React from "react";

const books = {
  Horror: [
    {
      title: "The Haunting of Hill House",
      author: "Shirley Jackson",
      image: "https://covers.openlibrary.org/b/id/8162601-L.jpg",
    },
    {
      title: "Pet Sematary",
      author: "Stephen King",
      image: "https://covers.openlibrary.org/b/id/8231654-L.jpg",
    },
    {
      title: "The Silent Patient",
      author: "Alex Michaelides",
      image: "https://covers.openlibrary.org/b/id/10543729-L.jpg",
    },
  ],
  Comedy: [
    {
      title: "Yes Please",
      author: "Amy Poehler",
      image: "https://covers.openlibrary.org/b/id/8231601-L.jpg",
    },
    {
      title: "Me Talk Pretty One Day",
      author: "David Sedaris",
      image: "https://covers.openlibrary.org/b/id/8231923-L.jpg",
    },
    {
      title: "Is Everyone Hanging Out Without Me?",
      author: "Mindy Kaling",
      image: "https://covers.openlibrary.org/b/id/10543820-L.jpg",
    },
  ],
  Fiction: [
    {
      title: "To Kill a Mockingbird",
      author: "Harper Lee",
      image: "https://covers.openlibrary.org/b/id/8226091-L.jpg",
    },
    {
      title: "The Book Thief",
      author: "Markus Zusak",
      image: "https://covers.openlibrary.org/b/id/8231627-L.jpg",
    },
    {
      title: "The Night Circus",
      author: "Erin Morgenstern",
      image: "https://covers.openlibrary.org/b/id/8231642-L.jpg",
    },
  ],
};

const Home = () => {
  return (
    <div
      className="home-container"
      style={{
        background: "linear-gradient(to bottom right, #f0f7ff, #e4f4ea)",
        minHeight: "100vh",
        padding: "30px 20px",
      }}
    >
      {/* Hero Section */}
      <header style={{ textAlign: "center", marginBottom: "50px" }}>
        <h1
          style={{
            fontSize: "3rem",
            color: "#1abc9c",
            letterSpacing: "1.5px",
            fontWeight: "bold",
          }}
        >
          ðŸŒ² Welcome to <span style={{ color: "#34495e" }}>Fable Forest</span>
        </h1>
        <p
          style={{
            fontSize: "1.2rem",
            color: "#555",
            maxWidth: "700px",
            margin: "15px auto",
            lineHeight: "1.7",
          }}
        >
          Explore genres, discover hidden gems, and lose yourself in a forest of timeless
          stories. âœ¨
        </p>
      </header>

      {/* Genres Section */}
      {Object.entries(books).map(([genre, bookList]) => (
        <section className="genre-section" key={genre} style={{ marginBottom: "60px" }}>
          <h2
            className="genre-title"
            style={{
              fontSize: "2rem",
              marginBottom: "20px",
              color: "#2c3e50",
              borderLeft: "6px solid #1abc9c",
              paddingLeft: "12px",
            }}
          >
            {genre} ðŸ“š
          </h2>
          <div
            className="books-horizontal-scroll"
            style={{
              display: "flex",
              gap: "20px",
              overflowX: "auto",
              paddingBottom: "10px",
            }}
          >
            {bookList.map((book, index) => (
              <div
                className="book-card"
                key={index}
                style={{
                  minWidth: "200px",
                  background: "#fff",
                  borderRadius: "15px",
                  padding: "15px",
                  textAlign: "center",
                  boxShadow: "0 4px 12px rgba(0,0,0,0.1)",
                  transition: "transform 0.2s ease, box-shadow 0.2s ease",
                  cursor: "pointer",
                }}
                onMouseEnter={(e) => {
                  e.currentTarget.style.transform = "translateY(-6px)";
                  e.currentTarget.style.boxShadow = "0 6px 18px rgba(0,0,0,0.15)";
                }}
                onMouseLeave={(e) => {
                  e.currentTarget.style.transform = "translateY(0)";
                  e.currentTarget.style.boxShadow = "0 4px 12px rgba(0,0,0,0.1)";
                }}
              >
                <img
                  src={book.image}
                  alt={book.title}
                  style={{
                    width: "100%",
                    height: "250px",
                    objectFit: "cover",
                    borderRadius: "10px",
                    marginBottom: "10px",
                  }}
                  loading="lazy"
                />
                <h4 style={{ fontSize: "1.1rem", color: "#2c3e50", margin: "5px 0" }}>
                  {book.title}
                </h4>
                <p style={{ fontSize: "0.95rem", color: "#777" }}>{book.author}</p>
              </div>
            ))}
          </div>
        </section>
      ))}

      {/* About Us */}
      <section
        className="about-us"
        style={{
          margin: "60px auto",
          padding: "40px 20px",
          maxWidth: "800px",
          textAlign: "center",
          background: "#ffffff",
          borderRadius: "15px",
          boxShadow: "0 4px 15px rgba(0,0,0,0.1)",
        }}
      >
        <h2 style={{ fontSize: "2rem", color: "#1abc9c", marginBottom: "15px" }}>
          About Us ðŸŒŸ
        </h2>
        <p style={{ fontSize: "1.1rem", color: "#444", lineHeight: "1.8" }}>
          At <strong>Fable Forest</strong>, we believe that every book is a doorway to
          another world. Our mission is to cultivate a community of readers where
          imagination thrives and knowledge grows. Whether you love horror, comedy,
          or timeless fiction, youâ€™ll find a story here that speaks to your soul. ðŸ“–âœ¨
        </p>
      </section>

      {/* Footer */}
      <footer
        className="footer"
        style={{
          marginTop: "60px",
          padding: "30px 20px",
          background: "#1abc9c",
          color: "#fff",
          textAlign: "center",
          borderRadius: "15px 15px 0 0",
        }}
      >
        <div className="footer-content">
          <p style={{ marginBottom: "15px", fontSize: "1.1rem" }}>ðŸ“¬ Connect with us:</p>
          <div
            className="footer-icons"
            style={{ display: "flex", justifyContent: "center", gap: "20px" }}
          >
            <a
              href="https://facebook.com"
              target="_blank"
              rel="noopener noreferrer"
              aria-label="Facebook"
            >
              <img
                src="https://cdn-icons-png.flaticon.com/512/145/145802.png"
                alt="Facebook"
                className="footer-icon-img"
                style={{ width: "30px", height: "30px" }}
              />
            </a>
            <a
              href="https://instagram.com"
              target="_blank"
              rel="noopener noreferrer"
              aria-label="Instagram"
            >
              <img
                src="https://cdn-icons-png.flaticon.com/512/2111/2111463.png"
                alt="Instagram"
                className="footer-icon-img"
                style={{ width: "30px", height: "30px" }}
              />
            </a>
            <a
              href="https://x.com"
              target="_blank"
              rel="noopener noreferrer"
              aria-label="X Twitter"
            >
              <img
                src="https://cdn-icons-png.flaticon.com/512/5968/5968830.png"
                alt="X Twitter"
                className="footer-icon-img"
                style={{ width: "30px", height: "30px" }}
              />
            </a>
          </div>
        </div>
        <div className="footer-bottom" style={{ marginTop: "15px", fontSize: "0.9rem" }}>
          <small>
            Â© {new Date().getFullYear()} Fable Forest Library. All rights reserved.
          </small>
        </div>
      </footer>
    </div>
  );
};

export default Home;
