import React, { useState } from 'react';
import axios from 'axios';

const books = [ /* Your 40-50 book objects here */ ];

const BookList = () => {
  const token = localStorage.getItem('token');
  const user = JSON.parse(localStorage.getItem('user'));
  const [purchasedBooks, setPurchasedBooks] = useState([]);

  const handleBuy = async (book) => {
    if (!token) {
      alert('Please login to buy books');
      return;
    }

    const confirm = window.confirm(`Do you want to buy "${book.title}"?`);
    if (!confirm) return;

    try {
      const res = await axios.post(
        'http://localhost:5000/api/books/buy',
        { book },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      alert('Book purchased!');
      setPurchasedBooks([...purchasedBooks, book]);
    } catch (err) {
      alert('Failed to purchase');
    }
  };

  return (
    <div className="book-list">
      <h2>Available Books</h2>
      <div className="book-grid">
        {books.map((book, i) => (
          <div key={i} className="book-card">
            <img src={book.image} alt={book.title} />
            <h4>{book.title}</h4>
            <p>{book.author}</p>
            <button onClick={() => handleBuy(book)}>Buy</button>
          </div>
        ))}
      </div>
    </div>
  );
};

export default BookList;
