import React from 'react';

const books = {
  Horror: [
    {
      title: "Dracula",
      author: "Bram Stoker",
      image: "https://images-na.ssl-images-amazon.com/images/I/71xbltFZzWL.jpg",
    },
    {
      title: "Frankenstein",
      author: "Mary Shelley",
      image: "https://images-na.ssl-images-amazon.com/images/I/81r+LNFXUUL.jpg",
    },
    {
      title: "The Shining",
      author: "Stephen King",
      image: "https://m.media-amazon.com/images/I/81E5lJX1uOL._SL1500_.jpg",
    },
  ],
  Comedy: [
    {
      title: "Good Omens",
      author: "Neil Gaiman & Terry Pratchett",
      image: "https://m.media-amazon.com/images/I/81lsAKKJgxL.jpg",
    },
    {
      title: "Bossypants",
      author: "Tina Fey",
      image: "https://m.media-amazon.com/images/I/81BPKQYfxwL.jpg",
    },
    {
      title: "Catch-22",
      author: "Joseph Heller",
      image: "https://m.media-amazon.com/images/I/81ZfOqKozjL.jpg",
    },
  ],
  Fiction: [
    {
      title: "The Alchemist",
      author: "Paulo Coelho",
      image: "https://images-na.ssl-images-amazon.com/images/I/71aFt4+OTOL.jpg",
    },
    {
      title: "1984",
      author: "George Orwell",
      image: "https://m.media-amazon.com/images/I/81Hlwk2Y3ML.jpg",
    },
    {
      title: "The Great Gatsby",
      author: "F. Scott Fitzgerald",
      image: "https://m.media-amazon.com/images/I/81xXAyG7trL.jpg",
    },
  ]
};

const Home = () => {
  return (
    <div className="home-container">
      <h2>ðŸ“– Welcome to the Fable Forest Library</h2>
      <p>Login or register to explore thousands of stories!</p>

      {Object.entries(books).map(([genre, bookList]) => (
        <div className="genre-section" key={genre}>
          <h3 className="genre-title">{genre}</h3>
          <div className="books-horizontal-scroll">
            {bookList.map((book, index) => (
              <div className="book-card" key={index}>
                <img className="book-image" src={book.image} alt={book.title} />
                <h4>{book.title}</h4>
                <p>{book.author}</p>
              </div>
            ))}
          </div>
        </div>
      ))}

      <footer className="footer">
        <p>ðŸ“š Connect with us:</p>
        <div className="footer-icons">
          <a href="https://facebook.com" target="_blank" rel="noopener noreferrer">
            <img src="https://cdn-icons-png.flaticon.com/512/733/733547.png" alt="Facebook" />
          </a>
          <a href="https://instagram.com" target="_blank" rel="noopener noreferrer">
            <img src="https://cdn-icons-png.flaticon.com/512/733/733558.png" alt="Instagram" />
          </a>
          <a href="https://x.com" target="_blank" rel="noopener noreferrer">
            <img src="https://cdn-icons-png.flaticon.com/512/733/733579.png" alt="X (Twitter)" />
          </a>
        </div>
      </footer>
    </div>
  );
};

export default Home;
