import React, { useState, useEffect, useCallback } from 'react';
import axios from 'axios';

const AdminDashboard = () => {
  const [admin, setAdmin] = useState(null);
  const [users, setUsers] = useState([]);
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');

  const token = localStorage.getItem('adminToken');

  const fetchUsers = useCallback(async () => {
    try {
      const res = await axios.get('/api/admin/users', {
        headers: { Authorization: `Bearer ${token}` },
      });
      setUsers(res.data);
    } catch (err) {
      console.error('Error fetching users:', err);
    }
  }, [token]);

  const handleLogin = async (e) => {
    e.preventDefault();
    try {
      const res = await axios.post('/api/admin/login', { email, password });
      localStorage.setItem('adminToken', res.data.token);
      setAdmin(res.data.admin);
      fetchUsers();
    } catch (err) {
      alert('Invalid admin credentials');
    }
  };

  const handleDeleteUser = async (id) => {
    try {
      await axios.delete(`/api/admin/users/${id}`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      setUsers((prevUsers) => prevUsers.filter((u) => u._id !== id));
    } catch (err) {
      console.error('Failed to delete user:', err);
    }
  };

  const handleLogout = () => {
    localStorage.removeItem('adminToken');
    setAdmin(null);
    setUsers([]);
  };

  useEffect(() => {
    if (token) {
      axios
        .get('/api/admin/me', {
          headers: { Authorization: `Bearer ${token}` },
        })
        .then((res) => {
          setAdmin(res.data.admin);
          fetchUsers();
        })
        .catch(() => {
          localStorage.removeItem('adminToken');
        });
    }
  }, [token, fetchUsers]);

  // Admin login form
  if (!admin) {
    return (
      <form onSubmit={handleLogin} style={{ padding: '30px' }}>
        <h2>üîê Admin Login</h2>
        <input
          type="email"
          placeholder="Admin Email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          required
        />
        <input
          type="password"
          placeholder="Admin Password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          required
        />
        <button type="submit">Login</button>
      </form>
    );
  }

  // Admin dashboard
  return (
    <div className="admin-dashboard" style={{ padding: '30px' }}>
      <h2>Welcome, {admin.name}</h2>
      <p>Email: {admin.email}</p>
      <button onClick={handleLogout} style={{ marginBottom: '20px' }}>üö™ Logout</button>

      <h3>üë• Registered Users</h3>
      {users.length === 0 ? (
        <p>No users found.</p>
      ) : (
        <ul style={{ listStyleType: 'none', paddingLeft: 0 }}>
          {users.map((user) => (
            <li key={user._id} style={{ marginBottom: '10px' }}>
              {user.name} ({user.email}){' '}
              <button
                onClick={() => handleDeleteUser(user._id)}
                style={{
                  marginLeft: '10px',
                  backgroundColor: 'crimson',
                  color: 'white',
                  border: 'none',
                  padding: '5px 10px',
                  cursor: 'pointer',
                }}
              >
                ‚ùå Delete
              </button>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default AdminDashboard;
