import React, { useState } from 'react';
import '../styles.css';

const books = [/* your full books array remains unchanged */];

const BookList = () => {
  const [search, setSearch] = useState('');

  const filteredBooks = books.filter(book =>
    book.title.toLowerCase().startsWith(search.toLowerCase())
  );

  return (
    <div className="booklist-container">
      <h2 className="section-title">📚 Library Book Collection</h2>

      <input
        type="text"
        placeholder="🔍 Search books by title..."
        className="search-bar"
        value={search}
        onChange={(e) => setSearch(e.target.value)}
      />

      <div className="booklist-grid">
        {filteredBooks.map((book, index) => (
          <div className="booklist-card" key={index}>
            <img src={book.image} alt={book.title} className="booklist-image" />
            <div className="booklist-info">
              <h3 className="book-title">{book.title}</h3>
              <p className="book-author">by {book.author}</p>
              <div className="genre-tag">{book.genre}</div>
              <div className="book-meta">
                <span className="price">{book.price}</span>
                <span className="rating">
                  {'★'.repeat(Math.floor(book.rating))}
                  {'☆'.repeat(5 - Math.floor(book.rating))}
                </span>
              </div>
              <button className="buy-btn">Buy Now</button>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default BookList;
