// AdminDashboard.jsx
import React, { useState, useEffect, useCallback } from "react";
import axios from "axios";
import "../styles.css";

const AdminDashboard = () => {
  const [admin, setAdmin] = useState(null);
  const [users, setUsers] = useState([]);
  const [books, setBooks] = useState([]);
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [loading, setLoading] = useState(true);

  const [title, setTitle] = useState("");
  const [author, setAuthor] = useState("");
  const [genre, setGenre] = useState("");
  const [price, setPrice] = useState("");
  const [image, setImage] = useState(null);
  const [pdf, setPdf] = useState(null);

  const token = localStorage.getItem("adminToken");

  const fetchUsers = useCallback(async () => {
    try {
      const res = await axios.get(
        "https://api-fable-forest.onrender.com/api/admin/users",
        {
          headers: { Authorization: `Bearer ${token}` },
        }
      );
      setUsers(res.data);
    } catch (err) {
      console.error("Error fetching users:", err);
    }
  }, [token]);

  const fetchBooks = useCallback(async () => {
    try {
      const res = await axios.get(
        "https://api-fable-forest.onrender.com/api/books"
      );
      setBooks(res.data);
    } catch (err) {
      console.error("Error fetching books:", err);
    }
  }, []);

  const handleLogin = async (e) => {
    e.preventDefault();
    try {
      const res = await axios.post(
        "https://api-fable-forest.onrender.com/api/admin/login",
        { email, password }
      );
      localStorage.setItem("adminToken", res.data.token);
      setAdmin(res.data.admin);
      fetchUsers();
      fetchBooks();
    } catch (err) {
      alert("Invalid admin credentials");
    }
  };

  const handleDeleteUser = async (id) => {
    if (!window.confirm("Are you sure you want to delete this user?")) return;
    try {
      await axios.delete(
        `https://api-fable-forest.onrender.com/api/admin/users/${id}`,
        {
          headers: { Authorization: `Bearer ${token}` },
        }
      );
      setUsers(users.filter((u) => u._id !== id));
    } catch (err) {
      console.error("Error deleting user:", err);
    }
  };

  const handleCreateBook = async (e) => {
    e.preventDefault();

    if (!image || !pdf) {
      alert("Please upload both image and PDF");
      return;
    }

    const formData = new FormData();
    formData.append("title", title);
    formData.append("author", author);
    formData.append("genre", genre);
    formData.append("price", price);
    formData.append("image", image);
    formData.append("pdf", pdf);

    try {
      const res = await axios.post(
        "https://api-fable-forest.onrender.com/api/admin/books",
        formData,
        {
          headers: {
            "Content-Type": "multipart/form-data",
            Authorization: `Bearer ${token}`,
          },
        }
      );
      alert(`Book "${res.data.title}" created successfully!`);

      setTitle("");
      setAuthor("");
      setGenre("");
      setPrice("");
      setImage(null);
      setPdf(null);
      fetchBooks();
    } catch (err) {
      console.error("Error creating book:", err.response || err);
      alert(err.response?.data?.message || "Failed to create book");
    }
  };

  const handleDeleteBook = async (id) => {
    if (!window.confirm("Are you sure you want to delete this book?")) return;
    try {
      await axios.delete(
        `https://api-fable-forest.onrender.com/api/admin/books/${id}`,
        {
          headers: { Authorization: `Bearer ${token}` },
        }
      );
      alert("Book deleted successfully");
      setBooks(books.filter((b) => b._id !== id));
    } catch (err) {
      console.error("Error deleting book:", err);
      alert("Failed to delete book");
    }
  };

  useEffect(() => {
    // load particles background
    window.particlesJS("particles-js", {
      particles: {
        number: { value: 80, density: { enable: true, value_area: 800 } },
        color: { value: "#00ffff" },
        shape: { type: "circle" },
        opacity: { value: 0.5 },
        size: { value: 3 },
        line_linked: { enable: true, color: "#00ffff", opacity: 0.4 },
        move: { enable: true, speed: 2 },
      },
      interactivity: {
        events: {
          onhover: { enable: true, mode: "repulse" },
          onclick: { enable: true, mode: "push" },
        },
      },
      retina_detect: true,
    });
  }, []);

  useEffect(() => {
    const fetchAdmin = async () => {
      if (!token) {
        setLoading(false);
        return;
      }
      try {
        const res = await axios.get(
          "https://api-fable-forest.onrender.com/api/admin/me",
          {
            headers: { Authorization: `Bearer ${token}` },
          }
        );
        setAdmin(res.data.admin);
        fetchUsers();
        fetchBooks();
      } catch (err) {
        console.error("Admin auth failed:", err);
        localStorage.removeItem("adminToken");
      } finally {
        setLoading(false);
      }
    };
    fetchAdmin();
  }, [fetchUsers, fetchBooks, token]);

  if (loading) return <p style={{ color: "white" }}>Loading...</p>;

  if (!admin) {
    return (
      <div className="login-container">
        <div id="particles-js"></div>
        <div
          className="login-box"
          style={{
            width: "320px",
            padding: "25px 20px",
            background: "transparent",
            border: "1px solid #00ffff",
            borderRadius: "10px",
            boxShadow: "0 0 15px rgba(0, 255, 255, 0.5)",
          }}
        >
          <h2 style={{ fontSize: "24px", marginBottom: "6px", color: "#00ffff" }}>
            ADMIN LOGIN
          </h2>
          <p style={{ fontSize: "12px", marginBottom: "15px", color: "#888" }}>
            Authorized access only
          </p>
          <form onSubmit={handleLogin}>
            <div className="input-box" style={{ marginBottom: "12px" }}>
              <label style={{ fontSize: "12px", color: "#888" }}>Email</label>
              <input
                type="email"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
                required
                style={{
                  padding: "6px 8px",
                  fontSize: "12px",
                  background: "transparent",
                  border: "1px solid #00ffff",
                  color: "#fff",
                  outline: "none",
                  width: "100%",
                }}
              />
            </div>
            <div className="input-box" style={{ marginBottom: "12px" }}>
              <label style={{ fontSize: "12px", color: "#888" }}>Password</label>
              <input
                type="password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                required
                style={{
                  padding: "6px 8px",
                  fontSize: "12px",
                  background: "transparent",
                  border: "1px solid #00ffff",
                  color: "#fff",
                  outline: "none",
                  width: "100%",
                }}
              />
            </div>
            <button
              type="submit"
              className="btn"
              style={{
                padding: "8px",
                fontSize: "13px",
                width: "100%",
                background: "#00ffff",
                color: "#000",
                border: "none",
                cursor: "pointer",
                fontWeight: "bold",
              }}
            >
              Sign In
            </button>
          </form>
        </div>
      </div>
    );
  }

  return (
    <div className="admin-dashboard" style={{ color: "white" }}>
      <header
        style={{
          marginBottom: "2rem",
          padding: "1rem",
          borderBottom: "2px solid #00ffff",
        }}
      >
        <h1 style={{ fontSize: "2rem", marginBottom: "0.5rem" }}>
          🛡️ Admin Control Panel
        </h1>
        <h2 style={{ fontSize: "1.5rem" }}>Welcome, {admin.name}</h2>
        <p style={{ fontStyle: "italic" }}>
          Main Administrator of Fable Forest Library
        </p>
      </header>

      <p>Email: {admin.email}</p>

      <div className="analytics">
        <h3>📊 Dashboard Analytics</h3>
        <p>
          Total Registered Users: <strong>{users.length}</strong>
        </p>
        <p>
          Total Created Books: <strong>{books.length}</strong>
        </p>
      </div>

      <h3>👥 Registered Users</h3>
      {users.length === 0 ? (
        <p>No users found.</p>
      ) : (
        <ul className="user-list">
          {users.map((user) => (
            <li key={user._id}>
              {user.name} ({user.email})
              <button onClick={() => handleDeleteUser(user._id)}>❌ Delete</button>
            </li>
          ))}
        </ul>
      )}

      <h3>📚 Created Books</h3>
      {books.length === 0 ? (
        <p>No books yet.</p>
      ) : (
        <ul className="book-list">
          {books.map((book) => (
            <li key={book._id}>
              {book.title} by {book.author}
              <button onClick={() => handleDeleteBook(book._id)}>
                ❌ Delete Book
              </button>
            </li>
          ))}
        </ul>
      )}

      <h3>➕ Create New Book</h3>
      <form onSubmit={handleCreateBook} className="book-form">
        <input
          type="text"
          placeholder="Title"
          value={title}
          onChange={(e) => setTitle(e.target.value)}
          required
        />
        <input
          type="text"
          placeholder="Author"
          value={author}
          onChange={(e) => setAuthor(e.target.value)}
          required
        />
        <input
          type="text"
          placeholder="Genre"
          value={genre}
          onChange={(e) => setGenre(e.target.value)}
          required
        />
        <input
          type="number"
          placeholder="Price"
          value={price}
          onChange={(e) => setPrice(e.target.value)}
          required
        />
        <input
          type="file"
          accept="image/*"
          onChange={(e) => setImage(e.target.files[0])}
          required
        />
        <input
          type="file"
          accept="application/pdf"
          onChange={(e) => setPdf(e.target.files[0])}
          required
        />
        <button type="submit">Create Book</button>
      </form>
    </div>
  );
};

export default AdminDashboard;
