// Frontend/pages/AdminDashboard.jsx
import React, { useState, useEffect, useCallback } from 'react';
import axios from 'axios';

const AdminDashboard = () => {
  const [admin, setAdmin] = useState(null);
  const [users, setUsers] = useState([]);
  const [books, setBooks] = useState([]);
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [loading, setLoading] = useState(true);

  const [title, setTitle] = useState('');
  const [author, setAuthor] = useState('');
  const [genre, setGenre] = useState('');
  const [price, setPrice] = useState('');
  const [image, setImage] = useState(null);
  const [pdf, setPdf] = useState(null);

  const token = localStorage.getItem('adminToken');

  const fetchUsers = useCallback(async () => {
    try {
      const res = await axios.get('/api/admin/users', { headers: { Authorization: `Bearer ${token}` } });
      setUsers(res.data);
    } catch (err) {
      console.error(err);
    }
  }, [token]);

  const fetchBooks = useCallback(async () => {
    try {
      const res = await axios.get('/api/books');
      setBooks(res.data);
    } catch (err) {
      console.error(err);
    }
  }, []);

  const handleLogin = async (e) => {
    e.preventDefault();
    try {
      const res = await axios.post('/api/admin/login', { email, password });
      localStorage.setItem('adminToken', res.data.token);
      setAdmin(res.data.admin);
      fetchUsers();
      fetchBooks();
    } catch (err) {
      alert('Invalid admin credentials');
    }
  };

  const handleDeleteUser = async (id) => {
    if (!window.confirm('Delete this user?')) return;
    try {
      await axios.delete(`/api/admin/users/${id}`, { headers: { Authorization: `Bearer ${token}` } });
      setUsers(users.filter((u) => u._id !== id));
    } catch (err) {
      console.error(err);
    }
  };

  const handleCreateBook = async (e) => {
    e.preventDefault();
    if (!image || !pdf) return alert('Upload both image and PDF');

    const formData = new FormData();
    formData.append('title', title);
    formData.append('author', author);
    formData.append('genre', genre);
    formData.append('price', price);
    formData.append('image', image);
    formData.append('pdf', pdf);

    try {
      const res = await axios.post('/api/admin/books', formData, {
        headers: { 'Content-Type': 'multipart/form-data', Authorization: `Bearer ${token}` },
      });
      alert(`Book "${res.data.title}" created!`);
      setTitle(''); setAuthor(''); setGenre(''); setPrice(''); setImage(null); setPdf(null);
      fetchBooks();
    } catch (err) {
      alert(err.response?.data?.message || 'Failed to create book');
    }
  };

  const handleDeleteBook = async (id) => {
    if (!window.confirm('Delete this book?')) return;
    try {
      await axios.delete(`/api/admin/books/${id}`, { headers: { Authorization: `Bearer ${token}` } });
      setBooks(books.filter((b) => b._id !== id));
    } catch (err) {
      alert('Failed to delete book');
    }
  };

  useEffect(() => {
    const fetchAdmin = async () => {
      if (!token) { setLoading(false); return; }
      try {
        const res = await axios.get('/api/admin/me', { headers: { Authorization: `Bearer ${token}` } });
        setAdmin(res.data.admin);
        fetchUsers();
        fetchBooks();
      } catch (err) {
        localStorage.removeItem('adminToken');
      } finally {
        setLoading(false);
      }
    };
    fetchAdmin();
  }, [fetchUsers, fetchBooks, token]);

  if (loading) return <p className="text-white text-center mt-10">Loading...</p>;

  if (!admin) return (
    <div className="min-h-screen flex items-center justify-center bg-black bg-gradient-to-br from-gray-900 to-gray-800">
      <form onSubmit={handleLogin} className="bg-gray-900 p-8 rounded-2xl shadow-2xl w-96 animate-fadeIn border border-indigo-500">
        <h2 className="text-3xl text-indigo-500 font-extrabold mb-6 text-center tracking-wide">ADMIN LOGIN</h2>
        <input type="email" placeholder="Email" value={email} onChange={e => setEmail(e.target.value)}
          className="w-full mb-4 p-3 rounded-xl bg-gray-800 text-white placeholder-gray-400 focus:outline-indigo-400 border border-gray-700 focus:ring-2 focus:ring-indigo-500" required />
        <input type="password" placeholder="Password" value={password} onChange={e => setPassword(e.target.value)}
          className="w-full mb-4 p-3 rounded-xl bg-gray-800 text-white placeholder-gray-400 focus:outline-indigo-400 border border-gray-700 focus:ring-2 focus:ring-indigo-500" required />
        <button type="submit" className="w-full bg-indigo-500 hover:bg-pink-500 text-white font-bold py-3 rounded-xl transition-all duration-300 shadow-lg hover:shadow-2xl transform hover:-translate-y-1">
          Login
        </button>
      </form>
    </div>
  );

  return (
    <div className="min-h-screen bg-black text-white p-8 space-y-8">
      <h1 className="text-5xl font-extrabold text-gradient bg-clip-text text-transparent bg-indigo-400 animate-pulse tracking-wide">
        Welcome, {admin.name}
      </h1>
      <p className="text-gray-400 mb-4">Email: {admin.email}</p>

      {/* Dashboard Analytics */}
      <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
        <div className="bg-gray-900 p-6 rounded-2xl shadow-xl hover:shadow-indigo-500/50 hover:scale-105 transition-transform duration-300">
          <h3 className="text-2xl font-bold text-indigo-400 mb-4">📊 Analytics</h3>
          <p>Total Users: <span className="font-bold text-pink-400">{users.length}</span></p>
          <p>Total Books: <span className="font-bold text-cyan-400">{books.length}</span></p>
        </div>

        <div className="bg-gray-900 p-6 rounded-2xl shadow-xl hover:shadow-pink-500/50 hover:scale-105 transition-transform duration-300 max-h-80 overflow-y-auto">
          <h3 className="text-2xl font-bold text-pink-400 mb-4">👥 Registered Users</h3>
          {users.length === 0 ? <p>No users</p> :
            <ul className="space-y-2">
              {users.map(u => (
                <li key={u._id} className="flex justify-between items-center bg-gray-800 p-2 rounded-xl hover:bg-gray-700 transition-colors">
                  <span>{u.name} ({u.email})</span>
                  <button onClick={() => handleDeleteUser(u._id)}
                    className="bg-red-500 hover:bg-red-600 px-3 py-1 rounded-xl font-bold transition-all duration-300 shadow-md hover:shadow-lg">Delete</button>
                </li>
              ))}
            </ul>
          }
        </div>
      </div>

      {/* Books Section */}
      <div className="bg-gray-900 p-6 rounded-2xl shadow-xl hover:shadow-cyan-500/50 hover:scale-105 transition-transform duration-300 max-h-96 overflow-y-auto">
        <h3 className="text-2xl font-bold text-cyan-400 mb-4">📚 Created Books</h3>
        {books.length === 0 ? <p>No books yet</p> :
          <ul className="space-y-2">
            {books.map(b => (
              <li key={b._id} className="flex justify-between items-center bg-gray-800 p-2 rounded-xl hover:bg-gray-700 transition-colors">
                <span>{b.title} by {b.author}</span>
                <button onClick={() => handleDeleteBook(b._id)}
                  className="bg-red-500 hover:bg-red-600 px-3 py-1 rounded-xl font-bold transition-all duration-300 shadow-md hover:shadow-lg">Delete Book</button>
              </li>
            ))}
          </ul>
        }
      </div>

      {/* Create Book Form */}
      <div className="bg-gray-900 p-6 rounded-2xl shadow-xl hover:shadow-indigo-500/50 hover:scale-105 transition-transform duration-300">
        <h3 className="text-2xl font-bold text-indigo-400 mb-4">➕ Create New Book</h3>
        <form onSubmit={handleCreateBook} className="grid grid-cols-1 md:grid-cols-2 gap-4">
          <input type="text" placeholder="Title" value={title} onChange={e => setTitle(e.target.value)}
            className="p-2 rounded-xl bg-gray-800 text-white focus:outline-indigo-400 border border-gray-700 focus:ring-2 focus:ring-indigo-500" required />
          <input type="text" placeholder="Author" value={author} onChange={e => setAuthor(e.target.value)}
            className="p-2 rounded-xl bg-gray-800 text-white focus:outline-indigo-400 border border-gray-700 focus:ring-2 focus:ring-indigo-500" required />
          <input type="text" placeholder="Genre" value={genre} onChange={e => setGenre(e.target.value)}
            className="p-2 rounded-xl bg-gray-800 text-white focus:outline-indigo-400 border border-gray-700 focus:ring-2 focus:ring-indigo-500" required />
          <input type="number" placeholder="Price" value={price} onChange={e => setPrice(e.target.value)}
            className="p-2 rounded-xl bg-gray-800 text-white focus:outline-indigo-400 border border-gray-700 focus:ring-2 focus:ring-indigo-500" required />
          <input type="file" accept="image/*" onChange={e => setImage(e.target.files[0])}
            className="p-2 text-white bg-gray-800 rounded-xl col-span-1 md:col-span-2" required />
          <input type="file" accept="application/pdf" onChange={e => setPdf(e.target.files[0])}
            className="p-2 text-white bg-gray-800 rounded-xl col-span-1 md:col-span-2" required />
          <button type="submit" className="bg-indigo-500 hover:bg-pink-500 text-white font-bold py-3 rounded-xl transition-all duration-300 shadow-lg hover:shadow-2xl transform hover:-translate-y-1 col-span-1 md:col-span-2">
            Create Book
          </button>
        </form>
      </div>
    </div>
  );
};

export default AdminDashboard;
