import React from 'react';
import './styles.css';

const genres = [
  {
    title: 'Science Fiction',
    books: [
      { title: 'Dune', author: 'Frank Herbert', image: 'https://m.media-amazon.com/images/I/41kTVLeW1CL.jpg' },
      { title: 'Neuromancer', author: 'William Gibson', image: 'https://m.media-amazon.com/images/I/51TnjKRRvQL.jpg' },
      { title: 'Ender\'s Game', author: 'Orson Scott Card', image: 'https://m.media-amazon.com/images/I/51itWwCB3cL.jpg' }
    ]
  },
  {
    title: 'Fantasy',
    books: [
      { title: 'The Hobbit', author: 'J.R.R. Tolkien', image: 'https://m.media-amazon.com/images/I/41aQPTCmeVL.jpg' },
      { title: 'Mistborn', author: 'Brandon Sanderson', image: 'https://m.media-amazon.com/images/I/51nJBNLPcpL.jpg' },
      { title: 'Harry Potter', author: 'J.K. Rowling', image: 'https://m.media-amazon.com/images/I/51UoqRAxwEL.jpg' }
    ]
  },
  {
    title: 'Mystery & Thriller',
    books: [
      { title: 'The Girl with the Dragon Tattoo', author: 'Stieg Larsson', image: 'https://m.media-amazon.com/images/I/41kY2vEBP9L.jpg' },
      { title: 'Gone Girl', author: 'Gillian Flynn', image: 'https://m.media-amazon.com/images/I/41Pyr7a5AfL.jpg' },
      { title: 'The Da Vinci Code', author: 'Dan Brown', image: 'https://m.media-amazon.com/images/I/51Mn3aZ26vL.jpg' }
    ]
  }
];

const Home = () => {
  return (
    <div className="home-container">
      <h2>Welcome to the Library</h2>
      <p>Explore our collection by genre</p>

      {genres.map((genre, index) => (
        <div key={index} className="genre-section">
          <h3 className="genre-title">{genre.title}</h3>
          <div className="books-horizontal-scroll">
            {genre.books.map((book, idx) => (
              <div key={idx} className="book-card">
                <img src={book.image} alt={book.title} className="book-image" />
                <h4>{book.title}</h4>
                <p>{book.author}</p>
              </div>
            ))}
          </div>
        </div>
      ))}
    </div>
  );
};

export default Home;
